Window.Script:
/*
 * 프로그램명 : frmCOM0030PProgressBar.xml
 * 설명 : Transactin 진행상태바를 보여준다.
 * 작성일 : 2009.06.24
 * 작성자 : 장성호
 */

#include "LIB::COMMON.js"; // 공통 관련

var start_time = Time();

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj);

    // Timer Setting
    SetTimer(1, 100);

    //Show Status가 False
    if (g_bShowProgressBar == false) {
        Close();
    }
}

function Form_OnUnloadCompleted(obj) {
    KillTimer(1); // Form Unload 시 Timer Kill

    gfn_Form_OnUnloadCompleted(obj);
}
/* 공통 필수 종료 */

function lfn_COM0030PProgressBar_OnTimer(obj,nEventID)
{
    lbPassed.text = (Time() - start_time) + " 초 경과";

    //Show Status가 False
    if (mb_ShowProgressBar == false) {
        Close();
    }
}
