Window.Script:
/*
 * 고객정보명 : frmCOM1000PTrainingPersonList.xml
 * 설명 : 고객정보검색팝업
 * 작성일 : 2009.07.23
 * 작성자 : 김동현
 */

#include "LIB::COMMON.js"; // 공통 관련

var sharchYN = "Y";  // 검색 조건 변경 여부
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

	if (IsExistVar("sYear")) {
        edYear.Text = sYear;
    }

    if (IsExistVar("sKeyword")) {
        edKeyword.Text = sKeyword;
        if (gfn_IsNull(sKeyword)) {
        } else {
            lfn_search(obj); // 고객정보 조회
        }
    }
    edKeyword.SetFocus();

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (gfn_IsNull(edKeyword.Text)) {
        gfn_Alert("키워드를 입력해 주십시오.");
        edKeyword.SetFocus();
        return false;
    }

    btnSelect.Enable = true;
    var serviceName = "otherincome:searchOtherPerson";

    tit_clearActionInfo();
	tit_addSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oOtherPerson=ds_oOtherPerson"	// outDs
		,"I_Keyword=" + quote(edKeyword.Text)
		,"lfn_AfterSearchOtherPerson"
		,false
		,""
		,true
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchOtherPerson(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oOtherPerson.GetCount() > 0) {
        gdOtherPerson.SetFocus();
    } else {
		if(confirm("조회된 자료가 없습니다.\n신규 소득자를 입력하시겠습니까?")) {
			Close();
		} else {
			edKeyword.SetFocus();
		}
    }
    sharchYN = "Y"; // 조회 후 선택 가능하도록 플래그 변경함
    btnSelect.Enable = true;  // 조회 후 선택 가능하게 함   
}

/* 고객 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnSelect_OnClick(obj) {
    if (ds_oOtherPerson.GetCount() < 1) {
        Close();
    }

    var arrArg = Array(5);
    arrArg[0] = ds_oOtherPerson.GetColumn(ds_oOtherPerson.currow, "OPMGNO");
    arrArg[1] = ds_oOtherPerson.GetColumn(ds_oOtherPerson.currow, "OPNM");
    arrArg[2] = ds_oOtherPerson.GetColumn(ds_oOtherPerson.currow, "BIRTHDAY");
    arrArg[3] = ds_oOtherPerson.GetColumn(ds_oOtherPerson.currow, "OPADDRESS");
    arrArg[4] = edYear.Text;

    Close(arrArg);
}

function gdOtherPerson_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if(sharchYN == "N") {
        alert("조회를 다시 하시고 선택 바랍니다.");
    } else {
        btnSelect_OnClick(obj);
    }
}

function edKeyword_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)
    }
}

function gdOtherPerson_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}
