#include "LIB::COMMON.js"; // 공통 관련

var sharchYN = "Y";  // 검색 조건 변경 여부
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sPersonNm")) {
		edPersonNm.Text = sPersonNm;
		medResidentNo.Value = sResidentNo;
		lfn_Search(obj); // 고객정보 조회
    }
    edPersonNm.SetFocus();
     
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
//        case 113 : // 조회(F2)
//            lfn_Search();
//            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    if (gfn_IsNull(edPersonNm.Text) && gfn_IsNull(medResidentNo.Value)) {
        gfn_Alert("성명과 주민등록번호(생년월일)를 입력해 주십시오.");
        edPersonNm.SetFocus();
        return false;
    }
    
	Https.Sync = true;    
    // 성명, 주민번호가 13자리이면 SCI DI값 가져오기
    var sDI = "";
    if (!gfn_IsNull(edPersonNm.Text) && !gfn_IsNull(medResidentNo.Value)) {	// 성명&주민번호 입력한 경우
		if (length(medResidentNo.Value) == 13) {	// 주민번호가 13자리이면
			Https.Sync = true;  
			sDI = gfn_SearchSCI(edPersonNm.Text, medResidentNo.Value);
			Https.Sync = false;
		} else {
			medResidentNo.SetFocus();
		}
	}
 	
 	if ((length(medResidentNo.Value) == 13) && (gfn_IsNull(sDI))) {
		gfn_Alert("실명인증 실패로 주민번호대체키 값을 수신받지 못했습니다.\n\n" +
		          "다시 조회해 주세요!\n\n" +
		          " * 실패사유 : " + gds_ioSciCheck.GetColumn(0, "STRCI2"));
		ds_oTrainingPerson.ClearData();
		medResidentNo.SetFocus();
		return false;
 	
 	}

 	// 주민번호 13자리이고 DI값이 있으면 조회
 	
    btnSelect.Enable = true;
    var serviceName = "com:searchPersonInfo";
     
    ds_oTrainingPerson.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingPerson=ds_oTrainingPerson"	// outDs
		,"TPNM=" + quote(edPersonNm.Text)
		 + " BIRTHDAY=" + quote(left(medResidentNo.Value,6))
		 + " HP=" + quote(edHp.Text)
		 + " DI=" + quote(sDI)	// args
		,"lfn_AfterSearchTrainingPerson"
		,false
		,""
		,true
	);
 	Https.Sync = false;
}


/*
 * 기능 : 조회
 */
function lfn_SearchKey(obj) {

    if (gfn_IsNull(medPersonKey.Value)) {
        gfn_Alert("개인식별번호를 입력해 주십시오.");
        medPersonKey.SetFocus();
        return false;
    }
    
    btnSelect.Enable = true;
    var serviceName = "com:searchPersonInfo";
     
    ds_oTrainingPerson.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingPerson=ds_oTrainingPerson"	// outDs
		,"PERSONKEY=" + quote(medPersonKey.Value)   // args
		,"lfn_AfterSearchTrainingPerson"
		,false
		,""
		,true
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingPerson(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
/*  // 조회시 한건일 경우 자동으로 화면 닫게 하였지만 주민번호 변경시 문제 발생으로 화면 뜨게 함(20091026)
    if (ds_oTrainingPerson.GetCount() == 1) {
        btnSelect_OnClick();
    }
 */   
    if (ds_oTrainingPerson.GetCount() > 0) {
        gdTrainingPerson.SetFocus();
    } else {
        edPersonNm.SetFocus();
    }
    sharchYN = "Y"; // 조회 후 선택 가능하도록 플래그 변경함
    btnSelect.Enable = true;  // 조회 후 선택 가능하게 함
    
    gfn_SearchMessage(gdTrainingPerson); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
   
}

/* 고객 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

