function gdTrainingPerson_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if(sharchYN == "N") {
        alert("조회를 다시 하시고 선택 바랍니다.");
    } else {
        btnSelect_OnClick(obj);
    }
}

function medResidentNo_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)

	}
    
}

function gdTrainingPerson_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}

function medResidentNo_OnChanged(obj,strText)
{
	// 길이가 주민번호와 일치하면 자동 조회 되게 하기 
	if (length(medResidentNo.Value) == "13") {
        //lfn_Search(); 
        
        if(ds_oTrainingPerson.count < 0) {
            medResidentNo.SetFocus();
        }
	 }
    
}

function medPersonKey_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
    if (nChar == 13) { // Enter Key
        lfn_SearchKey();	
    }
}

function medPersonKey_OnChanged(obj,strText)
{
	// 길이가 10자리이면 자동 조회 되게 하기 
	if (length(medPersonKey.Value) == "10") {
        //lfn_SearchKey(); 
        
        if(ds_oTrainingPerson.count < 0) {
            medPersonKey.SetFocus();
        }
	 }
}

function lfn_Cancel(obj)
{
	edPersonNm.Text = "";
	medResidentNo.Value = "";
	medPersonKey.Value = "";
	edHP.Text = "";
	ds_oTrainingPerson.ClearData();
}


function edPersonNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
    if (nChar == 13) { // Enter Key
        lfn_Search();
        medResidentNo.SetFocus();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)
    }
}
