Window.Script:
/*
 * 고객정보명 : frmCOM1000PTrainingPersonList.xml
 * 설명 : 고객정보검색팝업
 * 작성일 : 2009.07.23
 * 작성자 : 김동현
 */

#include "LIB::COMMON.js"; // 공통 관련

var sharchYN = "Y";  // 검색 조건 변경 여부
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sPersonNm")) {
        edPersonNm.Text = sPersonNm;
        medResidentNo.Value = sResidentNo;
        if (gfn_IsNull(sPersonNm) && gfn_IsNull(sResidentNo)) {
        } else {
            lfn_search(obj); // 고객정보 조회
           
        }
    }
    edPersonNm.SetFocus();
     
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    if (gfn_IsNull(edPersonNm.Text) && gfn_IsNull(medResidentNo.Value)) {
        gfn_Alert("성명이나 주민등록번호를 입력해 주십시오.");
        edPersonNm.SetFocus();
        return false;
    }
    
    btnSelect.Enable = true;
    var serviceName = "com:searchTrainingPerson";
    
    if(sPrgGubun = "22"){//시험
    
		serviceName = "com:searchExamPerson";
    
    }else{
		serviceName = "com:searchTrainingPerson";
    }
      
     
    ds_oTrainingPerson.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo(serviceName, false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oTrainingPerson=ds_oTrainingPerson"	// outDs
		,"TPNM=" + quote(edPersonNm.Text)
            + " TPRESIDENTNO=" + quote(replace(medResidentNo.Text,"-",""))// args
		,"lfn_AfterSearchTrainingPerson"
		,false
		,""
		,true
	);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchTrainingPerson(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
/*  // 조회시 한건일 경우 자동으로 화면 닫게 하였지만 주민번호 변경시 문제 발생으로 화면 뜨게 함(20091026)
    if (ds_oTrainingPerson.GetCount() == 1) {
        btnSelect_OnClick();
    }
 */   
    if (ds_oTrainingPerson.GetCount() > 0) {
        gdTrainingPerson.SetFocus();
    } else {
        edPersonNm.SetFocus();
    }
    sharchYN = "Y"; // 조회 후 선택 가능하도록 플래그 변경함
    btnSelect.Enable = true;  // 조회 후 선택 가능하게 함
    
    gfn_SearchMessage(gdTrainingPerson); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
   
}

/* 고객 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnSelect_OnClick(obj) {
    if (ds_oTrainingPerson.GetCount() < 1) {
        Close();
    }

    var arrArg = Array(30);
    arrArg[0] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPNM");
    arrArg[1] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRESIDENTNO");
    arrArg[2] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPMGNO");
    arrArg[3] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPTEL");
    arrArg[4] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPHPTEL");
    arrArg[5] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPEMAILID");
    arrArg[6] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPEMAILDOMAIN");
    arrArg[7] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPCUSTGUBUN");
    arrArg[8] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPLASTIMGSEQ");
    arrArg[9] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPAUTHYN");
    arrArg[10] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "IIDIRPATH");
    arrArg[11] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "IIREALNM");
    arrArg[12] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPZIPCD");
    if (ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDRGUBUN") == "0") {
		arrArg[13] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDR1");
		arrArg[14] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDR2");
    } else if (ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDRGUBUN") == "1") {
		arrArg[13] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPROADADDR1");
		arrArg[14] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPROADADDR2");
    } else {
		arrArg[13] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDR1");
		arrArg[14] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDR2");
    }
    arrArg[15] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPBULDNM");
    arrArg[16] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "IIREGDATE");
    arrArg[17] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPADDRGUBUN");
    arrArg[18] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRPNUCD");
    arrArg[19] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRROADBUILDMGNO");
    arrArg[20] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRROADDETAIL");
    arrArg[21] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRROADREMAIN");
    arrArg[22] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRSANGUBUN");
    arrArg[23] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRMAINBUNJI");
    arrArg[24] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRSUBBUNJI");
    arrArg[25] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRBUILDNM");
    arrArg[26] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRADDRETC");
    arrArg[27] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRCONVPGM");
    arrArg[28] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "TPRCONVSTATUS");
    arrArg[29] = ds_oTrainingPerson.GetColumn(ds_oTrainingPerson.currow, "FULLTPRESIDENTNO");
    Close(arrArg);
}

function gdTrainingPerson_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    if(sharchYN == "N") {
        alert("조회를 다시 하시고 선택 바랍니다.");
    } else {
        btnSelect_OnClick(obj);
    }
}

function edPersonNm_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
        medResidentNo.SetFocus();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)
    }
}

function medResidentNo_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {
    if (nChar == 13) { // Enter Key
        lfn_Search();
    } else {
        btnSelect.Enable = false;
        sharchYN = "N"; // 조회 조건 변경시 선택하여 진행 하지 못하도록 함(반드시 재조회 필요)

	}
    
}

function gdTrainingPerson_OnEnterDown(obj,nRow,nCell,strVal,nPivotIndex)
{
	btnSelect_OnClick();
}

function medResidentNo_OnChanged(obj,strText)
{
	// 길이가 주민번호와 일치하면 자동 조회 되게 하기 
	if (length(medResidentNo.Value) == "13") {
        lfn_Search(); 
        
        if(ds_oTrainingPerson.count < 0) {
            medResidentNo.SetFocus();
        }
	 }
    
}
