//2. sVamt
//3. sVpvmgno

var li_TpMgno = ""; // 강습자관리번호
var ls_IsNiceCheckOk = "N"; // 실명인증 체크
var jubsuYN = ""; // 접수대상자 유효 여부
var arrArg = array(3);         // TRAININGPERSON 변경 후 재 조회하여 값을 담음

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
	calSunapDate.Value = GetDate();
}

function Form_OnUnloadCompleted(obj) {
    close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
    }
}



/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(arrArg);
}
/* 공통 필수 종료 */

/* 사용자 함수 */




function btnEstimate_OnClick(obj)
{

    tit_clearActionInfo();
    tit_addSingleActionInfo("com:executeEstimatePublication", "", 0, "");
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,""    // inDs
        ,"ds_oEstimate=ds_oEstimate" // outDs
        , "GUBUN=" + quote(sVGubun)
          + " SUNAPDATE=" + quote(left(calSunapDate.Value, 8))
	      + " AMT=" + quote(sVamt)
	      + " PVVMMGNO=" + quote(sVpvmgno) // args
        ,"lfn_afterSearch"
        ,false
        ,false
        ,true
    );
               
	               
}

// 조회 후 callback/*
 * 기능 : POS 정상처리 후 처리 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_afterSearch(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        var arrRet = array(2);
        arrRet[0] = "FAIL";
        Close(arrRet);
        return;
    }

    if (ds_oEstimate.GetColumn(0, "O_RTNSTAT") <> "00") {
        gfn_ErrorMsg(ds_oEstimate.GetColumn(0, "O_RTNSTAT"), ds_oEstimate.GetColumn(0, "O_RTNMSG"));
        var arrRet = array(2);
        arrRet[0] = "FAIL";
        Close(arrRet);
        return;
    }
}
