var arrArg = array(3);         // TRAININGPERSON 변경 후 재 조회하여 값을 담음

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
	
	lfn_search();
}

function Form_OnUnloadCompleted(obj) {
    close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
    }
}



/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(arrArg);
}
/* 공통 필수 종료 */

/* 사용자 함수 */



function lfn_Search(obj) {

    tit_clearActionInfo();
    tit_addSearchActionInfo("com:searchEstimateList", false);
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,""    // inDs
        ,"ds_ioEstimateList=ds_oEstimateList"    // outDs
        , "PVVMMGNO=" + quote(sVpvmgno)	//args
        ,"lfn_afterSearch"
        ,false
        ,false
        ,true
    );
    
}

// 조회 후 callback/*
 * 기능 : POS 정상처리 후 처리 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_afterSearch(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

// 출력
function gdEstimateList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if (nCell == 7) {
	
		var retArr = gfn_EstimatePopup(ds_ioEstimateList.GetColumn(nRow, "VMYEAR"), 
									   ds_ioEstimateList.GetColumn(nRow, "VMJIBU"), 
									   ds_ioEstimateList.GetColumn(nRow, "VMBILLTYPE"), 
									   ds_ioEstimateList.GetColumn(nRow, "VMVATNO"));  //팝업 호출
		
	}
}
