Window.Script:
/*
 * 프로그램명 : frmtraining0026PTrainingSmsSendList.xml
 * 설명 : SMS발송현황팝업
 * 작성일 : 2009.09.18
 * 작성자 : 장성호
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (!IsExistVar("sMgNo")) {
        gfn_Alert("접수 정보가 없습니다. 다시 시도해 주십시오.");
        Close();
    }
    
    lfn_Search();	//메일발송현황 조회

}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

	if (sGubun == "T") { //강습접수
        ds_oEmailStat.ClearData(); // 데이타셋 초기화
        
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchEmailStatTrainingSingle", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oEmailStat=ds_oEmailStat"	// outDs
			,"TJMGNO=" + quote(sMgNo)
			,"lfn_AfterSearchEmailStat"
			,false
        );
    } else { // 인터넷 시험접수
        ds_oEmailStat.ClearData(); // 데이타셋 초기화      
   
        tit_clearActionInfo();
        tit_addCsvSearchActionInfo("training:searchEmailStatExamSingle", false);
        // 서버 호출 
        tit_callService(
            ""
            ,""
            ,""	// inDs
            ,"ds_oEmailStat=ds_oEmailStat"	// outDs
			,"EJMGNO=" + quote(sMgNo)
			,"lfn_AfterSearchEmailStatSingle"
			,false
        );
    }
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEmailStatSingle(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdEmailStat); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 사용자 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

function btnMutilSort_OnClick(obj)
{
	gfn_GridMultiSort(gdSmsStat, 0); // 멀티 정렬을 실행한다.
}

function gdEmailStat_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	lfn_ViewMailDetail();
}

/*
 * 기능 : 이메일 내용 상세 보기
 * parameter : 없음
 * return : 없음
 */
function lfn_ViewMailDetail(obj)
{
	var sArg = "sMailMgno=" + quote(ds_oEmailStat.GetColumn(ds_oEmailStat.currow, "ESHMGNO"));
	Dialog("training::frmtraining0711PMailDetailViewerPopup.xml", sArg, 1000, 700, "", -1, -1);	//이메일 상세보기 팝업을 호출한다.
}
