Window.Script:
/*
 * 프로그램명 : frmCOM3002PPOSProgressBar.xml
 * 설명 : 가상계좌 할당처리진행팝업
 * 작성일 : 2013.08.07
 * 작성자 : 정현호
 */

#include "LIB::COMMON.js"; // 공통 관련

var start_time = Time();
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj);

    lb_ShowProgressBar = true;

    // Timer Setting
    SetTimer(1, 100);

    parent.divPos.ds_ioPosOutput.ClearData();
    var lb_RetPosProgressBar = parent.divSETTLEBANKPos.lfn_POS(); // POS 호출

    if (lb_RetPosProgressBar == "OK") {
        lb_ShowProgressBar = false;
    } else {
        Close("FAIL");
    }

    //Show Status가 False
    if (lb_ShowProgressBar == false) {
        Close("OK");
    }
}

function Form_OnUnloadCompleted(obj) {
    KillTimer(1); // Form Unload 시 Timer Kill

    gfn_Form_OnUnloadCompleted(obj);
}
/* 공통 필수 종료 */

function lfn_ProgressBar_OnTimer(obj,nEventID)
{
    var ln_PassedTime = (Time() - start_time);
    lbPassed.text = ln_PassedTime + " 초 경과";

    if (ln_PassedTime > 10) { // 10초 경과 후 실패로 종료
        Close("TIMEOUT");
    }

    //Show Status가 False
    if (lb_ShowProgressBar == false) {
        Close("OK");
    }
}
