#include "LIB::COMMON.js"; // 공통 관련

function Form_OnLoadCompleted(obj) {
    radProcGubun_OnChanged();
}

function radProcGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
    switch (radProcGubun.Value) { // 단위업무프로그램
        case "01" : // 강습
            parent.medSunapAmount.Value = parent.sProcAmount;
            parent.medTotalSunapFee.Value = parent.sProcAmount;
            parent.edProcAmount.Text = " 강습" + NumFormat(toNumber(parent.sProcAmount)) + "원";
            break;
        case "02" : // 강습+시험
            parent.medSunapAmount.Value = toNumber(parent.sProcAmount) + 10000;
            parent.medTotalSunapFee.Value = toNumber(parent.sProcAmount) + 10000;
            parent.edProcAmount.Text = " 강습" + NumFormat(toNumber(parent.sProcAmount)) + "원 + 시험 10,000원";
            break;
    }
}
