Window.Script:
/*
 * 프로그램명 : frmCOM3033SSettlementGubunLcsSunap.xml
 * 설명 : 결제구분수첩수납
 * 작성일 : 2009.09.13
 * 작성자 : 김동현
 */

#include "LIB::COMMON.js"; // 공통 관련

function Form_OnLoadCompleted(obj) {

    if (sKey2 = "1") {
        ds_oProcGubun.SetColumn(0, "DATA", "신규 수납");
        ds_oProcGubun.SetColumn(1, "DATA", "신규 환불");
    } else {
        ds_oProcGubun.SetColumn(0, "DATA", "재발급 수납");
        ds_oProcGubun.SetColumn(1, "DATA", "재발급 환불");
    }
    
    if( sSunapGubun == "1" ){
        ds_oProcGubun.Filter("CD=='B'");
        radProcGubun.Value = "B";
        parent.medSunapAmount.Value = parent.sRepayAmount;
    } else {
        ds_oProcGubun.Filter("CD=='0'");
        radProcGubun.Value = "0";
        parent.medSunapAmount.Value = parent.sProcAmount;
    }

    parent.medTotalSunapFee.Value = parent.sProcAmount;
    parent.edProcAmount.Text = " 수첩" + NumFormat(toNumber(parent.sProcAmount)) + "원";

}
