#include "LIB::COMMON.js"; // 공통 관련

function Form_OnLoadCompleted(obj) {

	// LG U+ 가상계좌 폐지, 세틀뱅크 가상계좌 사용
	if (left(getDate(), 8) >= "20130820") {
		ds_oSunapGubun.DeleteRow(3);
		
		ds_oSunapGubun.InsertRow(3);
		ds_oSunapGubun.SetColumn(3, "CD", "4");
		ds_oSunapGubun.SetColumn(3, "DATA", "가상계좌");
	}
	
}

function radSunapGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") { // 현금
        divSunabDtl.Url = "";//COM::frmCOM3101SSettlementPOSSunap.xml";
        radBankSunapGubun.Enable = true;
        parent.radRecieptYn.Enable = true;
	} else if (strCode == "1") { // 카드
        divSunabDtl.Url = "COM::frmCOM3102SSettlementPOSSunap.xml";
        radBankSunapGubun.Value = "0";
        radBankSunapGubun.Enable = false;
        parent.radRecieptYn.Enable = true;
	} else if (strCode == "2") { // 현금영수증
        divSunabDtl.Url = "COM::frmCOM3103SSettlementPOSSunap.xml";
        radBankSunapGubun.Enable = true;
        parent.radRecieptYn.Enable = true;
	} else if (strCode == "3") { // 가상계좌(LG U+)
        divSunabDtl.Url = "COM::frmCOM3104SSettlementPOSSunap.xml";
        radBankSunapGubun.Enable = false;
        parent.radRecieptYn.Enable = false;
        parent.radRecieptYn.Value = "N";
	} else if (strCode == "4") { // 가상계좌(신규 세틀뱅크)
        divSunabDtl.Url = "COM::frmCOM3105SSettlementPOSSunap.xml";
        radBankSunapGubun.Enable = false;
        parent.radRecieptYn.Enable = false;
        parent.radRecieptYn.Value = "N";
	}
}

// 수납방법
function radBankSunapGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") {
		radSunapGubun.Enable = true;
	} else if (strCode == "4") {
		radSunapGubun.Value = 0;
		radSunapGubun.Enable = false;
	}	
}
