Window.Script:
/*
 * 강습접수정보명 : frmCOM4000PRexpertViewer.xml
 * 설명 : 렉스퍼트뷰어팝업
 * 작성일 : 2009.08.12
 * 작성자 : 장성호
 */

#include "LIB::COMMON.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    this.Title = "출력(" + sReportFileName + ")";

    // 렉스퍼트를 실행한다.
	var lb_Ret = gfn_OpenReport(sReportFileName, gfn_MakeRexDataXml(sReportDatasetMap), sParams, rxpRexpert,sSubReport, sTopMargin, sLeftMargin);
	if (!lb_Ret) { // 파일이 없는 경우
        Close();
	}
}

function Form_OnUnloadCompleted(obj) {
    Close(lb_IsPrinted);
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
    }
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close(lb_IsPrinted);
}

function Form_OnSize(obj,nCx,nCy,nState)
{
    if (gs_IsResizingForm == "Y") {
        rxpRexpert.Height = nCy - rxpRexpert.Top - 4;
        rxpRexpert.Width = nCx - rxpRexpert.Left - 4;
        btnEnd.Left = nCx - btnEnd.Width - 6;
    }
}
/* 공통 필수 종료 */

/* 사용자 함수 */

/* 사용자 이벤트 함수 */
