Window.Script:
/*
 * 프로그램명 : frmCOM6100PrivacyManage.xml
 * 설명 : 개인정보동의서 등록
 * 작성일 : 2013.04.01
 * 작성자 : 정현호
 */

#include "LIB::training.js"; // 공통업무 관련

var ls_FileUploadFolder = "/PRTDATA/";

var ls_FileUploadPath = "Privacy/" + left(GetDate(), 4); // 개인정보동의서 이미지경로;
var ls_UploadFileName = ""; //서버에 저장될 파일 명

var ls_RealFilePath = ""; // 등록된 개인정보동의서 이미지경로;

var ls_RealFileName = ""; // 등록된 개인정보동의서 이미지 실제파일명
var lb_FileUpload = false; // 개인정보동의서 유무

var ls_ResidentNoError = "";	// 주민번호 유효성 여부 0:정상 1:비정상

/*
// 받아온 파라미터
sWorkGubun  // 단위업무
sBizGubun 	// 업무구분
sCourse		// 직능구분
sPersonNM	// 성명
sResidentNo	// 주민번호
sBizMgno1	// 관련업무 관리번호1
sBizMgno2   // 관련업무 관리번호2
*/


function Form_OnLoadCompleted(obj)
{
	
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.	
	
	
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }
	
	gfn_SearchPrivacyWork(ds_oBizGubun, sWorkGubun);	// 단위업무 조회
	
	cbxBizGubun.Value = sBizGubun;		// 단위업무구분
	
	gfn_SearchPrivacyCourse(ds_oPrivacyCourse, ds_oBizGubun.GetColumn(ds_oBizGubun.FindRow("CD",cbxBizGubun.Value), "GROUPCD"));	// 직능조회
	
	cbxCourse.Value = ds_oPrivacyCourse.GetColumn(ds_oPrivacyCourse.FindRow("COURSECD", sCourse), "CD");
	
	lbJubsuInfo.Text = "개인정보동의서 등록 이력";
	
	edPersonNm.Text = sPersonNM;	// 성명
	
	medResidentNo.Value = sResidentNo;	// 주민번호
		
	lfn_ResidentNoCheck();		// 주민번호 유효성 체크

	lfn_search(); // 등록된 최종자료가 있는지 조회

}



/*
 * 기능 : 버튼 활성/비활성시킨다.
 * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성)
 * return : 없음
 */
function lfn_SetFormAuth(bStatus) {
    btnDelete.Enable = bStatus; // 삭제 버튼
    btnNewSave.Enable = bStatus; // 신규저장 버튼
    
    btnOldSave.Enable = bStatus; // 기존자료수정 버튼
}

// 초기 자료 조회
function lfn_search(obj) {

	var ls_SearchArg = "PPNM=" + quote(sPersonNM)
	
	    + " PPERSONKEY=" + quote(sResidentNo)
	    
	    + " PACOURSECD=" + quote(cbxCourse.Value);
	    

    ds_ioPrivacyAgree.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
    tit_addSearchActionInfo("com:searchPrivacyAgree", false);
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,""    // inDs
        ,"ds_ioPrivacyAgree=ds_oPrivacyAgree"    // outDs
        ,ls_SearchArg
        ,"lfn_AfterSearchPrivacyAgree"
        ,false
        ,false
        ,true
    );

}


function lfn_AfterSearchPrivacyAgree(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return ;
    }
	
	if (ds_ioPrivacyAgree.RowCount() > 0 ) {	// 기존자료가 있으면
	
		btnNewSave.Enable = true;
		
		btnOldSave.Enable = true;
		
		btnDelete.Enable = true;
		
		mb_IsNewData = false;	//자료있음 신규아님
		
		if (!gfn_isNull(ds_ioPrivacyAgree.GetColumn(0, "RUMGNO"))) {
		
			edAgreeFileYN.Text = "등록됨";
			
			btnFileView.Enable = true;
			
			btnFileDelete.Enable = true;
			
			ls_RealFilePath = ls_FileUploadFolder + ds_ioPrivacyAgree.GetColumn(0, "RUPATH"); // 등록된 개인정보동의서 이미지경로

			ls_RealFileName = ds_ioPrivacyAgree.GetColumn(0, "RUFILENM"); // 등록된 개인정보동의서 이미지 실제파일명
			
		} else {
			
			edAgreeFileYN.Text = "미등록";
			
			btnFileView.Enable = false;
			
			btnFileDelete.Enable = false;
			
			ls_RealFilePath = "";
			
			ls_RealFileName = "";
		
		}
	
		radAgreeGubun.Value = ds_ioPrivacyAgree.GetColumn(0, "PHPROCSTATUS");	// 동의여부
		
		radAgreeGubun.Enable = true;
	
		calProcDate.Value = ds_ioPrivacyAgree.GetColumn(0, "PHPROCDATE");	// 처리일자
		
		lbStatus.Text = "등록완료";
		
		lbStatus.BkColor = "mediumseagreen";
		
	} else {	// 조회자료가 없으면(신규)
	
		btnNewSave.Enable = true;
		
		btnOldSave.Enable = false;
		
		btnDelete.Enable = false;
		
		edAgreeFileYN.Text = "미등록";
		
		btnFileView.Enable = false;
		
		btnFileDelete.Enable = false;
		
		radAgreeGubun.Value = 0;	// 동의여부
		
		radAgreeGubun.Enable = false;
		
		calProcDate.Value = left(GetDate(), 8);		// 처리일자
		
		mb_IsNewData = true;	// 자료없음 신규
		
		lbStatus.Text = "미등록";
		
		lbStatus.BkColor = "coral";
	}
	
	edImageFile.Text = "";
	
	lfn_searchPrivacyAgreeH();  //  단위업무별 동의서 등록 이력 조회.
	
}

// 단위업무별 동의서 등록 이력 조회
function lfn_searchPrivacyAgreeH(obj) {

	var ls_SearchArg = "PPNM=" + quote(sPersonNM)
	
	    + " PPERSONKEY=" + quote(sResidentNo)
	    
	    + " PABIZCD=" + quote(cbxBizGubun.Value);

    ds_ioPrivacyAgreeH.ClearData(); // 데이타셋 초기화
    tit_clearActionInfo();
    tit_addSearchActionInfo("com:searchPrivacyAgreeH", false);
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,""    // inDs
        ,"ds_ioPrivacyAgreeH=ds_oPrivacyAgreeH"    // outDs
        ,ls_SearchArg
        ,"lfn_AfterSearchPrivacyAgreeH"
        ,false
        ,false
        ,true
    );


}


function lfn_AfterSearchPrivacyAgreeH(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return ;
    }
    
}



// 주민번호 유효성 체크
function lfn_ResidentNoCheck(obj)
{
	var lb_ValidRedidentNoYn = gfn_CheckResidentNo(medResidentNo.Value); // 주민번호체계 검사

    var lb_ValidForeignNoYn = gfn_CheckForeignNo(medResidentNo.Value); // 재외국인 등록번호 검사

    if (!lb_ValidRedidentNoYn && !lb_ValidForeignNoYn) { // 둘다 실패면

		lfn_ResidentNoChange(false);		

    } else {

        lfn_ResidentNoChange(true);

    }
}

// 주민번호 오류/정상 표시
function lfn_ResidentNoChange(bStatus) {
	
	if(bStatus) {	//정상

		lbResidentNoError.Text = "정상";

		lbResidentNoError.Color = "blue";
		
		ls_ResidentNoError = "0";

	} else {	//오류

		lbResidentNoError.Text = "오류";

		lbResidentNoError.Color = "brown";
		
		ls_ResidentNoError = "1";

	}

	lbResidentNoError.Visible = true;
}

// 주민등록번호
function medResidentNo_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam) {
	if (nChar == 13) {
	
		lfn_ResidentNoCheck();		// 주민번호 유효성 체크
		btnFileSearch.SetFocus();

	}

}

function medResidentNo_OnKillFocus(obj)
{
    if (!gfn_IsNull(edPersonNm.Text) && !gfn_IsNull(medResidentNo.Value)) {
        
        lfn_ResidentNoCheck();
        
    }
}

// 스캔파일찾기
function btnFileSearch_OnClick(obj)
{

	fdAttachFile.Type = "OPEN";
	if (fdAttachFile.open()) {
		//alert(fdAttachFile.FilePath + "\\" + fdAttachFile.FileName); => (D:\한국소방\보고문서\화면.ppt)
		fiAttachFile.FileName = fdAttachFile.FilePath + "\\" + fdAttachFile.FileName;
		edImageFile.text = fdAttachFile.FileName;
		fiAttachFile.Close();
		lb_FileUpload = true;
	}

}


// 신규저장
function lfn_NewSave(obj)
{

	
	if (gfn_IsNull(edImageFile.text)) {
	
		gfn_Alert("개인정보동의서 파일이 없습니다. \n\n[파일찾기] 버튼을 눌러 파일을 찾아 주세요!");
	
		return false;
	
	}
	
	if (!gfn_Confirm("신규 저장 하시겠습니까?")) {
		return false;
	}

	
	ls_UploadRealName = substr(medResidentNo.Value,0,6) +"_"+ getDate() + gfn_GetFileExtensionName(fdAttachFile.FileName);
	
    if (lb_FileUpload) { //파일 등록일 경우	

		 tit_FileWrite( hfAttachFile
					 , ls_FileUploadFolder + ls_FileUploadPath
					 , fdAttachFile.FilePath
					 , fdAttachFile.FileName
					 , ls_UploadRealName
					 , "U"
					 , false
					 );
		hfAttachFile.CloseFile();
    }
    
    // 입력 데이터셋에 값을 넣는다.
    
    ds_iPrivacyAgree.ClearData();
    
    ds_iPrivacyAgree.InsertRow(0);
    
    ds_iPrivacyAgree.SetColumn(0, "PPNM", sPersonNM);
    
    ds_iPrivacyAgree.SetColumn(0, "PPRESIDENTNO", sResidentNo);
    
    ds_iPrivacyAgree.SetColumn(0, "PPSTATUS", ls_ResidentNoError);
    
    ds_iPrivacyAgree.SetColumn(0, "NEWDATA", mb_IsNewData);	// mb_IsNewData  1:신규 0:기존자료 있음
    
    ds_iPrivacyAgree.SetColumn(0, "PABIZCD", cbxBizGubun.Value);
    
    ds_iPrivacyAgree.SetColumn(0, "PACOURSECD", cbxCourse.Value);
    
    ds_iPrivacyAgree.SetColumn(0, "PHPROCSTATUS", radAgreeGubun.Value);		// 0:동의 1:동의철회
    
    ds_iPrivacyAgree.SetColumn(0, "PHPROCDATE", calProcDate.Value);
    
    ds_iPrivacyAgree.SetColumn(0, "PHPROCMETHOD", "1");
    
    ds_iPrivacyAgree.SetColumn(0, "PHMGMTPK1", sBizMgno1);
    
    ds_iPrivacyAgree.SetColumn(0, "PHMGMTPK2", sBizMgno2);
    
    ds_iPrivacyAgree.SetColumn(0, "RUPATH", ls_FileUploadPath);
    
    ds_iPrivacyAgree.SetColumn(0, "RUFILENM", ls_UploadRealName);
    
    tit_clearActionInfo();
    tit_addSingleActionInfo("com:insertPrivacyAgree", "", 0, "");
    

    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iPrivacyAgree=ds_iPrivacyAgree"    // inDs
        ,"ds_oPrivacyAgreeResult=ds_oPrivacyAgree"    // outDs
        ,""    // args
        ,"lfn_AfterInsertPrivacyAgree"
        ,false
        ,false
        ,true
    );

}

// 신규저장 후 처리
function lfn_AfterInsertPrivacyAgree(nErrorCode, strErrorMsg)
{

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    switch (ds_oPrivacyAgreeResult.GetColumn(0, "RESULTCODE")) {
    
        case "OK" : // 정상처리
        
            gfn_SaveMessage(); // 저장 메세지를 처리한다.
            lb_FileUpload = false;
            
            lfn_Search();	// 조회
            
            break;
    }
}



// 기존자료 수정
function lfn_OldSave(obj)
{
	
	if (!gfn_Confirm("현재 자료를 수정 하시겠습니까?")) {
		return false;
	}
	
    if (lb_FileUpload) { //새로 파일을 등록한 경우	

        if (Length(ls_RealFileName) > 0) { //기존 동의서 파일이 있는경우 삭제
			// 기존파일 삭제
            tit_FileDelete( ls_RealFilePath
                          , ls_RealFileName
                          , false
                          );
		}
		
		ls_UploadRealName = substr(medResidentNo.Value,0,6) +"_"+ getDate() + gfn_GetFileExtensionName(fdAttachFile.FileName);
				// 새로운파일 저장
		tit_FileWrite( hfAttachFile
					 , ls_FileUploadFolder + ls_FileUploadPath
					 , fdAttachFile.FilePath
					 , fdAttachFile.FileName
					 , ls_UploadRealName
					 , "U"
					 , false
					 );
		hfAttachFile.CloseFile();
    }
    
    // 입력 데이터셋에 값을 넣는다.
    
    ds_iPrivacyAgree.ClearData();
    
    ds_iPrivacyAgree.InsertRow(0);
    
    ds_iPrivacyAgree.SetColumn(0, "PPNM", sPersonNM);
    
    ds_iPrivacyAgree.SetColumn(0, "PPRESIDENTNO", sResidentNo);
    
    ds_iPrivacyAgree.SetColumn(0, "PPSTATUS", ls_ResidentNoError);
    
    ds_iPrivacyAgree.SetColumn(0, "NEWDATA", mb_IsNewData);	// mb_IsNewData  1:신규 0:기존자료 있음
    
    ds_iPrivacyAgree.SetColumn(0, "PHPROCSTATUS", radAgreeGubun.Value);		// 0:동의 1:동의철회
    
    ds_iPrivacyAgree.SetColumn(0, "PHPROCDATE", calProcDate.Value);		// 처리일자
    
    ds_iPrivacyAgree.SetColumn(0, "PAMGNO", ds_ioPrivacyAgree.GetColumn(0, "PAMGNO"));		// 동의관리번호
    
    ds_iPrivacyAgree.SetColumn(0, "PHSEQ", ds_ioPrivacyAgree.GetColumn(0, "PHSEQ"));		// 처리일자
    
    
    if (lb_FileUpload) {	//새로 파일을 등록한 경우	
    
		ds_iPrivacyAgree.SetColumn(0, "RUMGNO", ds_ioPrivacyAgree.GetColumn(0, "RUMGNO"));		// 업로드실제파일경로
    
		ds_iPrivacyAgree.SetColumn(0, "RUPATH", ls_FileUploadPath);		// 업로드실제파일경로
		
		ds_iPrivacyAgree.SetColumn(0, "RUFILENM", ls_UploadRealName);	// 업로드실제파일명
    
    }
    
    tit_clearActionInfo();
    tit_addSingleActionInfo("com:updatePrivacyAgree", "", 0, "");
    

    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iPrivacyAgree=ds_iPrivacyAgree"    // inDs
        ,""    // outDs
        ,""    // args
        ,"lfn_AfterUpdatePrivacyAgree"
        ,false
        ,false
        ,true
    );    
   
}


// 기존자료 수정 후 처리
function lfn_AfterUpdatePrivacyAgree(nErrorCode, strErrorMsg)
{

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
	gfn_Alert("정상적으로 수정되었습니다.");
    
    lb_FileUpload = false;
    lfn_Search();	// 조회

}

// 등록파일 삭제
function btnFileDelete_OnClick(obj)
{

	if (gfn_IsNull(ls_RealFileName)) {
	
		gfn_Alert("삭제할 개인정보동의서 파일이 없습니다.");
		
		return false;
		
	}

	if (!gfn_Confirm("개인정보 동의서 등록파일을 삭제하시겠습니까?")) { // 삭제 하시겠습니까?
		return false;
	}

	if (tit_FileDelete( ls_RealFilePath
	
	                  , ls_RealFileName
	                  
	                  , true)) {
				  
		gfn_Alert("파일이 정상적으로 삭제되었습니다.");

		lb_FileUpload = false;
		
    } else {
    
		gfn_Alert("파일 삭제에 실패했습니다.");
    
    }

}

// 파일 보기
function btnFileView_OnClick(obj)
{
    gfn_ImageViewPopup(
        ls_RealFilePath
        , ls_RealFileName
        , ls_RealFileName
    );
}


// 삭제
function lfn_Delete(obj)
{

	if (!gfn_Confirm("개인정보동의 자료를 삭제 하시겠습니까? \n\n 해당 업무 직능의 최종 자료만 삭제됩니다.")) {
		return false;
	}


	// 개인정보동의서 파일삭제
	if (!gfn_IsNull(ls_RealFileName)) {

		tit_FileDelete( ls_RealFilePath
					  , ls_RealFileName
					  , false
					  );

	
	}
	

    // 입력 데이터셋에 값을 넣는다.
    
    ds_iPrivacyAgreeDelete.ClearData();
    
    ds_iPrivacyAgreeDelete.InsertRow(0);
    
    ds_iPrivacyAgreeDelete.SetColumn(0, "PPNM", sPersonNM);
    
    ds_iPrivacyAgreeDelete.SetColumn(0, "PPRESIDENTNO", sResidentNo);
    
    ds_iPrivacyAgreeDelete.SetColumn(0, "PAMGNO", ds_ioPrivacyAgree.GetColumn(0, "PAMGNO"));
    
    ds_iPrivacyAgreeDelete.SetColumn(0, "PHSEQ", ds_ioPrivacyAgree.GetColumn(0, "PHSEQ"));
    
    ds_iPrivacyAgreeDelete.SetColumn(0, "RUMGNO", ds_ioPrivacyAgree.GetColumn(0, "RUMGNO"));
    

    tit_clearActionInfo();
    tit_addSingleActionInfo("com:deletePrivacyAgree", "", 0, "");
    

    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_iPrivacyAgreeDelete=ds_iPrivacyAgreeDelete"    // inDs
        ,""    // outDs
        ,""    // args
        ,"lfn_AfterDeletePrivacyAgree"
        ,false
        ,false
        ,true
    );

}



// 삭제 후 처리
function lfn_AfterDeletePrivacyAgree(nErrorCode, strErrorMsg)
{

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
	gfn_Alert("정상적으로 삭제되었습니다.");
    
    lb_FileUpload = false;
    lfn_Search();	// 조회

}

// 동의여부를 변경했을 경우
function radAgreeGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (mb_IsNewData == false) {
	
		if (ds_ioPrivacyAgree.GetColumn(0, "PHPROCSTATUS") <> nNewIndex) {
		
			btnOldSave.Enable = false;		
		
		} else {
		
			btnOldSave.Enable = true;
		
		}
	
	} 
	
	if (nNewIndex == 0) {
	
		lbRegistGubun.Text = "개인정보 동의서 등록여부";
		
		lbRegist.Text = "개인정보 동의서 등록";
	
	} else if (nNewIndex == 1) {
	
		lbRegistGubun.Text = "개인정보 동의철회서 등록여부";
		
		lbRegist.Text = "개인정보 동의철회서 등록";
		
	}
	
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}

function lfn_End(obj)
{
	close(true);
}
