/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sGTMGNO")) {

        lfn_search(obj); // 고객정보 조회

    }

}

function Form_OnUnloadCompleted(obj) {
    Close("END");
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
        case 113 : // 조회(F2)
            lfn_Search();
            break;
    }
}

/*
 * 기능 : 조회
 */
 function lfn_Search() {

	ds_oDownloadPopup.ClearData(); // 데이타셋 초기화

	tit_clearActionInfo();
	tit_addSearchActionInfo("com:searchPersonalDownloadPopup", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oDownloadPopup=ds_oDownloadPopup" // outDs
		,"GTMGNO=" + quote(sGTMGNO)
		 + " REGDATE8=" + quote(sREGDATE8)
		 + " HOUR=" + quote(sHOUR)
		 + " SABUN=" + quote(sSABUN)
		 + " PCCD=" + quote(sPCCD)
		 	// args
		,"lfn_AfterDownloadPopup"
		,false
		,""
		,true
	);

 }

 function lfn_AfterDownloadPopup(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    medTotalCnt.Value = ds_oDownloadPopup.GetRowCount();

}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close("END");
}
/* 공통 필수 종료 */

/* 사용자 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchCustomerInfo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    if (ds_oCustomer.GetCount() < 1) {

		btnSelect.Enable = false;  // 조회 후 선택 가능하게 함
		gfn_SearchMessage(gdCustomer); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

    } else if (ds_oCustomer.GetCount() = 1) {

        btnSelect_OnClick();

    } else if (ds_oCustomer.GetCount() > 1) {

        gdCustomer.SetFocus();

		btnSelect.Enable = true;  // 조회 후 선택 가능하게 함

		gfn_SearchMessage(gdCustomer); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
    }


}

/* 고객 이벤트 함수 */
function lfn_SortGrid_OnHeadClick(obj,nCell,nX,nY,nPivotIndex) {
    var lobj_Dataset = object(obj.BindDataset);
    // 데이터가 있는 경우 해당 컬럼을 기준으로 정렬한다.
    if (lobj_Dataset.GetRowCount() > 1) {
        gfn_GridCellSort(obj, nCell); // 해당 셀정렬을 호출한다.
    }
}

