Window.Script:
/*
 * 프로그램명 : frmCOM9070MPersonalDownloadResult.xml
 * 설명 : 개인정보 다운로드 점검결과 확인
 * 작성일 : 2019.12.10
 * 작성자 : 정현호
 */

#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

//    gfn_SearchJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
    // 부서정보 조회 
    lfn_DeptSearch();
    
    cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");    
    
    
    
    var currYear = toNumber(left(getDate(), 4));
    ds_ioYear.ClearData();
    
    for (var i=-4; i<=1; i++) {
        ds_ioYear.InsertRow(0);
        ds_ioYear.SetColumn(0, "CD", currYear + i);
        ds_ioYear.SetColumn(0, "DATA", currYear + i);
    }
    
	if (substr(getDate(), 4, 2) == "01") { 
		cbxYear.Value = toNumber(currYear)-1;
		cbxMonth.Value = "12";
	} else {
		cbxYear.Value = currYear;
		cbxMonth.Value = lpad(toNumber(substr(getDate(), 4, 2))-1, "0", 2);
	}

    cbxJibu.SetFocus();

}

/**
 * 폼 사이즈
 */
function Form_OnSize(obj,nCx,nCy,nState) {
}

/**
 * 버튼 실행 권한
 */
function lfn_SetFormAuth(bStatus) {
}


/////////////////////////////////////////////////////////////////////////////////
// 기본 버튼
/////////////////////////////////////////////////////////////////////////////////

/**
 * 초기화
 */
function lfn_Cancel(obj) {
    cbxJibu.Value = gds_oUserInfo.GetColumn(0, "PDEPTCD");
    cbxYear.Value = currYear;
    cbxMonth.Value = substr(getDate(), 4, 2);
    dsCloseCheck.SetCellProp("head", 0, "text", 0);
}

/*
 * 기능 : 부서정보 조회
 */
function lfn_DeptSearch(obj) {

    ds_ioJibu.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("sys:search0093DeptList", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioJibu=ds_ioDeptList"	// outDs
		,""
		,"lfn_AfterDeptSearch"
		,false
	);
}

/*
 * 기능 : 부서정보 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterDeptSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	// 부서정보 '전체'
	ds_ioJibu.InsertRow(0);
	ds_ioJibu.SetColumn(0,"DEPTNM","전체");
}

/**
 * 조회
 */
function lfn_Search(obj) {

    dsCloseCheck.SetCellProp("head", 0, "text", 0);

    ds_oDownloadChk.ClearData();
    tit_clearActionInfo();
    tit_addSearchActionInfo("com:searchPersonalDownloadChk", false);
    tit_callService(
        ""
        ,""
        ,""	// inDs
        ,"ds_oDownloadChk=ds_oDownloadChk"	// outDs
        ,"PDCGTMGNO=" + quote(cbxJibu.Value)
            + " PDCYEAR=" + quote(cbxYear.Value)
            + " PDCMONTH=" + quote(cbxMonth.Value)	// args
        ,""
        ,false
        ,false
        ,true
    );
    
}

/**
 * 확인처리
 */
function btnProc_OnClick(obj) {
    
    ds_iDownloadChk.ClearData();
    ds_oDownloadChk.Filter("SEL='1'");
    ds_iDownloadChk.CopyF(ds_oDownloadChk);
    ds_oDownloadChk.UnFilter();
    
    if (ds_iDownloadChk.GetRowCount() < 1) {
        gfn_Alert("확인할 자료를 선택해 주십시오.");
        return;
    }
    
    if (!gfn_Confirm("확인처리 하시겠습니까?")) {
        return;
    }

    for (var i=0; i<=ds_iDownloadChk.GetRowCount(); i++) {
        ds_iDownloadChk.SetColumn(i, "PDCCLOSEGUBUN", "0");
    }

    tit_clearActionInfo();
    tit_addMultiActionInfo("com:updatePersonalDownloadResult", "", 0, "", "N");
    tit_callService(
        ""
        ,""
        ,"ds_iDownloadChk=ds_iDownloadChk"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSave"
        ,false
        ,false
        ,true
    );	

}

/////////////////////////////////////////////////////////////////////////////////
// 공통
/////////////////////////////////////////////////////////////////////////////////

/**
 * 저장 후 처리
 */
function lfn_AfterSave(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        gfn_Alert("오류가 발생되었습니다. 정보지원과에 문의 하시기 바랍니다.");
        return "Transaction Error!";
    }
    
    gfn_Alert("결과 확인이 완료되었습니다.");
    lfn_Search();
}

function cbxMonth_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam) {

    if (nChar = "13") {
        lfn_Search();
    }
    
	gfn_EnterNextFocus(obj,nChar,bShift,bCtrl,bAlt,LLParam,HLParam);
}

// 확인취소
function btnProcCancel_OnClick(obj)
{
    
    ds_iDownloadChk.ClearData();
    ds_oDownloadChk.Filter("SEL='1'");
    ds_iDownloadChk.CopyF(ds_oDownloadChk);
    ds_oDownloadChk.UnFilter();
    
    if (ds_iDownloadChk.GetRowCount() < 1) {
        gfn_Alert("확인취소할 자료를 선택해 주십시오.");
        return;
    }
    
    if (!gfn_Confirm("확인취소 하시겠습니까?")) {
        return;
    }

    for (var i=0; i<=ds_iDownloadChk.GetRowCount(); i++) {
        ds_iDownloadChk.SetColumn(i, "PDCCLOSEGUBUN", "1");
    }

    tit_clearActionInfo();
    tit_addMultiActionInfo("com:updatePersonalDownloadResult", "", 0, "", "N");
    tit_callService(
        ""
        ,""
        ,"ds_iDownloadChk=ds_iDownloadChk"	// inDs
        ,""	// outDs
        ,""	// args 
        ,"lfn_AfterSave"
        ,false
        ,false
        ,true
    );	
}

function lfn_Print(obj)
{
    if (ds_oDownloadChk.RowCount() < 1) {
        gfn_Alert("출력할 데이터가 없습니다.");
        return false;
    }
   
    gfn_CallReportPopup("frmCOM9070R01PersonalDownloadResult.rex"
                      , "ds_oDownloadChk"
                      , "");
}
