// 구분코드 조회 
function lfn_searchCode(obj) {
	ds_ioMNFCTRETCGubun.ClearData();		// 제조소 구분
	ds_ioMNFCTRETCGubunD.ClearData();		// 제조소 상세구분 
	ds_ioMNFCTRETCGubunD.ClearData();		// 업체구분
 
    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust5020ObjGubun", false);
    tit_addSearchActionInfo("cust:searchCust6100MnfctretcSeCode", false);
    tit_addSearchActionInfo("cust:searchCust6100MnfctretcDetailSeCode", false);
    tit_addSearchActionInfo("cust:searchCust5020CompanyGubun", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_ioObjGubun=ds_oObjGubun ds_ioMNFCTRETCGubun=ds_oMNFCTRETCGubun ds_ioMNFCTRETCGubunD=ds_oMNFCTRETCGubunD ds_ioCompanyGubun=ds_oCompanyGubun" // outDs
        , ""	//args
        , ""	//callBackFunc
        ,false
        ,false
        ,true
    );
}


// 구분코드값 변경시 
function cbxCodeGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	lfn_FilterMnfctretcGubun(strCode);
}

// 제조소 상세구분 필터링
function lfn_FilterMnfctretcGubun(sFilterGubun) {	
	ds_ioMNFCTRETCGubunD.UnFilter();
	
	//  필터링
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
		cbxCodeGubunD.Enable = true;
        ls_FilterExpr += "CODE_GROUP='" + trim(sFilterGubun) + "'";    

		ds_ioMNFCTRETCGubunD.Filter(ls_FilterExpr);        
		
		if( ds_ioMNFCTRETCGubun.FindRow("DATA", cbxCodeGubun.Text) == -1) {
			cbxCodeGubunD.Text = "";
		}
		
		if (ds_ioMNFCTRETCGubunD.GetColumn(0,"DATA") == "전체") {
			ds_ioMNFCTRETCGubunD.AddRow(0);
		} else {
			ds_ioMNFCTRETCGubunD.InsertRow(0);
		}
		
		ds_ioMNFCTRETCGubunD.SetColumn(0,"DATA","전체");
		ds_ioMNFCTRETCGubunD.SetColumn(0,"CODE_GROUP",trim(sFilterGubun));  	  
		cbxCodeGubunD.Index = 0;
    }  else {
		cbxCodeGubunD.Enable = false;
    }
}

//기능 : 조회
function lfn_Search(obj) {
    ds_oList.ClearData(); // 데이타셋 초기화
    ds_ioFireSurvey.Clear();
    ds_ioFireSurveyAdministratorInfo.Clear();
    ds_ioFireSurveyLicenseInfo.Clear();
    ds_ioFireSurveyRelationInfo.Clear(); 
	ds_ioFireSurveyDong.Clear();
	ds_ioFireSurveyFloorStatus.Clear();
	ds_ioFireSurveySFloorStatus.Clear();
	ds_ioFireSurveyFloorE.Clear();
	
	ds_ioDanMnfctretcInfo.Clear();
	ds_ioDanTankInfo.Clear();	    
	ds_ioDanStatusSuccession.Clear();
	ds_ioDanManagerInfo.Clear();
	     	  
	ds_ioFightFireCompany.Clear();
	ds_ioFightFireCompanyPossessionHuman.Clear();   // 소방업체(소방기술자)
	ds_ioFightFireCompanyFireLicenseInfo.Clear();
    
    

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust5020FireList", false);
	
	// 문자열 포함여부
	var KEYWORD = "";
	if (edKeyWord.Text != "") {		
		if (chkPerson.Value == "1") {
			KEYWORD = "%"+edKeyWord.Text+"%";
		} else {
			KEYWORD = edKeyWord.Text+"%";
		}
	}

	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oList=ds_oList"	// outDs
		,"CTCD=" + quote(substr(cbxHead.Value,0,2))   
		  + " BUILDING=" + quote(radGubun.Value) 
		  + " STATION=" + quote(cbxStation.Value)
		  + " CENTER=" + quote(cbxCenter.Value)
		  + " SEARCHGUBUN=" + quote(radAgent.Value) 
          + " KEYWORD=" + quote(KEYWORD)
          + " ROWNUM=" + quote(edCount.Text)  
          + " CODE=" + quote(cbxCodeGubun.Value)
          + " CODED=" + quote(cbxCodeGubunD.Value)
          + " PBLINSTT=" + quote(cbxMFPBLINSTT.Value)
		,"lfn_AfterSearch"
		,false
	);
}

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */                                    
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    // 조회 로그 
    logWrite("1");
    
    medTotalCnt = ds_oList.getcount();
}

