//기능 : 조회대상 정보 조회 (상세)
function lfn_FormSearch(obj) {
      
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    
    ds_ioFireSurvey.Clear();
    ds_ioFireSurveyAdministratorInfo.Clear();
    ds_ioFireSurveyLicenseInfo.Clear();
    ds_ioFireSurveyRelationInfo.Clear(); 
	ds_ioFireSurveyDong.Clear();
	ds_ioFireSurveyFloorStatus.Clear();
	ds_ioFireSurveySFloorStatus.Clear();
	ds_ioFireSurveyFloorE.Clear();
	
	ds_ioDanMnfctretcInfo.Clear();
	ds_ioDanTankInfo.Clear();	    
	ds_ioDanStatusSuccession.Clear();
	ds_ioDanManagerInfo.Clear();
	     	  
	ds_ioFightFireCompany.Clear();
	ds_ioFightFireCompanyPossessionHuman.Clear();   // 소방업체(소방기술자)
	ds_ioFightFireCompanyFireLicenseInfo.Clear();
          
	tit_clearActionInfo();	
   
	var outds = "";
	if (radGubun.Value == "1") {
		tit_addSearchActionInfo("cust:searchCust5010FireSurvey", false);                 //1) 소방방제청 방화관리대상물> > 개황 조회
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyAdministratorInfo", false);//2) 소방방제청 방화관리대상물 >> 개황 >> 방화관리자정보
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyLicenseInfo", false);      //3) 소방방제청 방화관리대상물 >> 개황 >> 방화관리자정보 >> 보유자격증
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyRelationInfo", false);     //4) 소방방제청 방화관리대상물 >> 개황 >> 관계자정보	
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyDong", false);             //5) 소방방제청 방화관리대상물 >> 동 현황 조회	
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyFloorStatus", false);      //6) 소방방제청 방화관리대상물 >> 층별 현황 조회	
		tit_addSearchActionInfo("cust:searchCust5010FireSurveySFloorStatus", false);     //7) 소방방제청 방화관리대상물 >> 특수용도별 현황 조회	
		tit_addSearchActionInfo("cust:searchCust5010FireSurveyFloorE", false);           //8) 소방방제청 방화관리대상물 >> 층설비별 내역 조회	
		
		outds = "ds_ioFireSurvey=ds_oFireSurvey ds_ioFireSurveyAdministratorInfo=ds_oFireSurveyAdministratorInfo"
				+ " ds_ioFireSurveyLicenseInfo=ds_oFireSurveyLicenseInfo ds_ioFireSurveyRelationInfo=ds_oFireSurveyRelationInfo"
				+ " ds_ioFireSurveyDong=ds_oFireSurveyDong ds_ioFireSurveyFloorStatus=ds_oFireSurveyFloorStatus"
				+ " ds_ioFireSurveySFloorStatus=ds_oFireSurveySFloorStatus ds_ioFireSurveyFloorE=ds_oFireSurveyFloorE";
		
	} else if (radGubun.Value == "2" || radGubun.Value == "3") {		// 위험물
		tit_addSearchActionInfo("cust:searchCust5010DanMnfctretcInfo", false);           //9) 소방방제청 방화관리대상물 >> 허가정보등 기본정보 조회 >> 기본정보
		tit_addSearchActionInfo("cust:searchCust5010DanTankInfo", false);                //10) 소방방제청 위험물         >> 허가정보등 기본정보 조회 >> 탱크유별현황
		tit_addSearchActionInfo("cust:searchCust5010DanStatusSuccession", false);        //11) 소방방제청 위험물         >> 허가정보등 기본정보 조회 >> 지위승계
		tit_addSearchActionInfo("cust:searchCust5010DanManagerInfo", false);             //12) 소방방제청 위험물        >> 허가정보등 기본정보 조회 >> 선임자	
		outds = "ds_ioDanMnfctretcInfo=ds_oDanMnfctretcInfo ds_ioDanTankInfo=ds_oDanTankInfo"  
				+ " ds_ioDanStatusSuccession=ds_oDanStatusSuccession ds_ioDanManagerInfo=ds_oDanManagerInfo";
	} else if (radGubun.Value == "4") {
		// 소방 관리자
		tit_addSearchActionInfo("cust:searchCust5010FightFireCompany", false);                  //11) 소방방제청 소방업체(소방기술자)  >> 기본정보
		tit_addSearchActionInfo("cust:searchCust5010FightFireCompanyPossessionHuman", false);   //12)소방방제청 소방업체(소방기술자)              >> 보유인력
		tit_addSearchActionInfo("cust:searchCust5010FightFireCompanyFireLicenseInfo", false); //13)소방방제청 소방업체(소방기술자)              >> 방화관리자		
		
		outds = "ds_ioFightFireCompany=ds_oFightFireCompany ds_ioFightFireCompanyPossessionHuman=ds_oFightFireCompanyPossessionHuman"  
				+ " ds_ioFightFireCompanyFireLicenseInfo=ds_oFightFireCompanyFireLicenseInfo";
	} else {
		return;
	}
   
    
    

	
	
	// 서버 호출 
	tit_callService( 
		""
		,""
		,""	// inDs
		,outds// outDs	  	  
	    ,"SerialNumber=" + quote(sMFPIS1)//quote("28000000052646")//quote(sSerialNumber)   //-- 일련번호(매개변수)
      +  " SerialNumberDanger=" + quote(sMFPIS1)//quote("MF261994000020")//quote(sSerialNumber)   //-- 위험물 일련번호(매개변수)  //MF282008000060	// args 
      +  " SerialNumberES=" + quote(sMFPIS3)
      +  " SerialNumberEDS=" + quote(sMFPIS4)
		,"lfn_AftersearchCust"
		,false
	);
	
}

/*
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AftersearchCust(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    // 상세 로그 남기기 (오픈)
    detailLogWrite();
	
	
    // 위험물안전관리자 건물 동.층
    var bulddong_floor_sn = "";
	if (ds_ioDanMnfctretcInfo.GetColumn(0,"BULDDONG_SN") != null){
		bulddong_floor_sn = ds_ioDanMnfctretcInfo.GetColumn(0,"BULDDONG_SN")+"동";
		if (ds_ioDanMnfctretcInfo.GetColumn(0,"FLOOR_SN") != null) {
			bulddong_floor_sn = bulddong_floor_sn + " "+ds_ioDanMnfctretcInfo.GetColumn(0,"FLOOR_SN")+"층";
		}
	}
	
	tabTab1.tabpgTab1Page2.edBULDDONG_FLOOR_SN.Text = bulddong_floor_sn;	
		
	// 층별정보 필터링 
	var Expr = "";
	Expr = "BULDDONG_SN="+ds_ioFireSurveyDong.GetColumn(0,"BULDDONG_SN");
	tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page3.cbxDongList.Value = ds_ioFireSurveyDong.GetColumn(0,"BULDDONG_SN");
	ds_ioFireSurveyFloorStatus.Filter(Expr);
	ds_ioFireSurveySFloorStatus.Filter(Expr);
	ds_ioFireSurveyFloorE.Filter(Expr);
	
	// 특수용도별피터링
	if (ds_ioFireSurveyFloorStatus.GetColumn(0,"FLOOR_SN") != "" && ds_ioFireSurveyFloorStatus.GetColumn(0,"FLOOR_SN") != null) {
		Expr = "FLOOR_SN="+ds_ioFireSurveyFloorStatus.GetColumn(0,"FLOOR_SN");
		ds_ioFireSurveySFloorStatus.Filter(Expr);
	}
	
	// 소방안전관리자 자격증
	Expr = "QUALFUSER_SN='"+ds_ioFireSurveyAdministratorInfo.GetColumn(0,"QUALFUSER_SN")+"'";	
	ds_ioFireSurveyLicenseInfo.Filter(Expr);
	
	// 소방기술자
	Expr = "QUALFUSER_SN='"+ds_ioFightFireCompanyPossessionHuman.GetColumn(0,"QUALFUSER_SN")+"'";
	ds_ioFightFireCompanyFireLicenseInfo.Filter(Expr);
	
	
	
	
}

function tabTab1_tabpgTab1Page1_tabTab1_tabpgTab1Page3_cbxDongList_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var Expr = "";
	EXpr = "BULDDONG_SN="+tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page3.cbxDongList.Value;
	ds_ioFireSurveyFloorStatus.UnFilter();
	ds_ioFireSurveySFloorStatus.UnFilter();
	ds_ioFireSurveyFloorE.UnFilter();
	ds_ioFireSurveyFloorStatus.Filter(EXpr);
	ds_ioFireSurveySFloorStatus.Filter(EXpr);	
	ds_ioFireSurveyFloorE.Filter(EXpr);
	
	EXpr = "FLOOR_SN="+ds_ioFireSurveyFloorStatus.GetColumn(0,"FLOOR_SN");
	ds_ioFireSurveySFloorStatus.Filter(EXpr);
	
}

