	ExportObj1.ExportFileName	 = "c:\\KFSA\\excel\\temp\\temp_excel.xls";	
	ExportObj1.MakeEmptyFileWhenNotExist = false;			
	
	if (tabTab1.TabIndex == 0) { 
		
		ExportObj1.AddExportGrid("층설비내역" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page3.tabTab1.tabpgTab1Page2.gdFloorE, true, false);
