		ExportObj1.AddExportGrid("층별현황" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page3.tabTab1.tabpgTab1Page1.gdFloorStatus, true, false);		
		ExportObj1.AddExportGrid("동현황" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page2.gdDongStatus , true, false);
		ExportObj1.AddExportGrid("건물개황_관계자" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page2.gdRelation, true, false);
		ExportObj1.AddExportGrid("건물개황_자격증" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page1.gdLicense, true, false);
		ExportObj1.AddExportGrid("건물개황_소방안전관리자" + "!" + "A1", tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page1.tabTab1.tabpgTab1Page1.gdAdminList, true, false);
		
		
		
	} else if (tabTab1.TabIndex == 1) { 
	
		if (sCourse == "30") {	
			ExportObj1.AddExportGrid("지위승계" + "!" + "A1", tabTab1.tabpgTab1Page2.tabTab1.tabpgTab1Page4.gdStatusSucces, true, false);
			ExportObj1.AddExportGrid("탱크유별현황" + "!" + "A1", tabTab1.tabpgTab1Page2.tabTab1.tabpgTab1Page3.gdTankInfo, true, false);
			ExportObj1.AddExportGrid("선임자정보" + "!" + "A1", tabTab1.tabpgTab1Page2.tabTab1.tabpgTab1Page1.gdManagerInfo, true, false);
		} else if (sCourse == "80") {
			ExportObj1.AddExportGrid("선임자정보" + "!" + "A1", tabTab1.tabpgTab1Page2.tabTab2.tabpgTab2Page3.gdManagerInfo , true, false);
			ExportObj1.AddExportGrid("지위승계" + "!" + "A1", tabTab1.tabpgTab1Page2.tabTab2.tabpgTab2Page2.gdStatusSucces, true, false);
		}
	
	} else if (tabTab1.TabIndex == 2) { 
	
		ExportObj1.AddExportGrid("저격증" + "!" + "A1", tabTab1.tabpgTab1Page3.gdLicenscInfo , true, false);
		ExportObj1.AddExportGrid("기술자" + "!" + "A1", tabTab1.tabpgTab1Page3.gdPHuman, true, false);
		
	}
	
	ExportObj1.Export();
	ExportObj1.Close();
	ExportObj1 = null;
}

// 최상단 탭 변경 하지 못하도록 막는 로직 
function tabTab1_OnChanged(obj,nOldIndex,nNewindex)
{
	if (radGubun.Value == "1") {   
        tabTab1.TabIndex = 0; 
    } else if (radGubun.Value == "2" || radGubun.Value == "3") {
        tabTab1.TabIndex = 1; 
		 if (radGubun.Value == "2") {
			tabTab1.tabpgTab1Page2.tabTab1.Visible = "true";
			tabTab1.tabpgTab1Page2.tabTab2.Visible = "false";
        } else {
			tabTab1.tabpgTab1Page2.tabTab1.Visible = "false";
			tabTab1.tabpgTab1Page2.tabTab2.Visible = "true";
        }
    } else if (radGubun.Value == "4") {
        tabTab1.TabIndex = 2;  
    }

}

/**************************************************************
 조건조회 로그 남기기 
**************************************************************/
function logWrite(gubun) {
	var ls_HistoryArg = "";
	var Action = "";
	
	// 조회이력을 저장한다.  
	ls_HistoryArg = " 구분=" + radGubun.Value;
	ls_HistoryArg += ", 소방본부=" + cbxHead.Value;
	ls_HistoryArg += ", 소방서=" + cbxStation.Value;
	ls_HistoryArg += ", 소방센터=" + cbxCenter.Value;
	ls_HistoryArg += ", 코드구분=" + cbxCodeGubun.Value;
	ls_HistoryArg += ", 코드상세구분=" + cbxCodeGubunD.Value;
	ls_HistoryArg += ", 검색조건=" + radAgent.Value;
	ls_HistoryArg += ", 검색어=" + edKeyWord.Text;
	Action = "청자료 전체조회";
	
	var menuNM = "";
	
	if (sGubun == "customer") {
		menuNM = "고객정보관리 청조회";
	} else {
		menuNM = "데이타연계관리 소방방재청";
	}
	
    gfn_InsertFPISSearchPrintHistory(
        "S"
        , this.ID
        , menuNM
        , Action
        , ls_HistoryArg
    );
}

/**************************************************************
 상세조회 로그 남기기 
**************************************************************/
function detailLogWrite() {
	
	var gubun = ""; 		// 대상구분
	var searchNm = "";		// 조회대상명 	
	
	// 대상구분 (소방안전관리자 , 위험물안전관리자 , 위험물운송자 , 소방기술자)
	if (radGubun.Value == "1") {   
		gubun = 0;
		searchNm = ds_ioFireSurvey.GetColumn(ds_ioFireSurvey.currow,"OBJ_NM");
	} else if (radGubun.Value== "2") {
		gubun = 1;
		
		if (ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"OBJ_NM") == null) {
			if (ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"INSTLR_CPR_NM") == null) {
				if (ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"INSTLR_NM") != null) {
					searchNm = ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"INSTLR_NM");
				}
			} else {
				searchNm = ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"INSTLR_CPR_NM");
			}
		} else {
			searchNm = ds_ioDanMnfctretcInfo.GetColumn(ds_ioDanMnfctretcInfo.currow,"OBJ_NM");
		}
		
    } else if (radGubun.Value == "3") {
		gubun = 2;
		searchNm = ds_oList.GetColumn(ds_oList.currow,"OBJ_NM");
    } else if (radGubun.Value == "4"){
		gubun = 3;
		searchNm = ds_oList.GetColumn(ds_oList.currow,"OBJ_NM");
    }

	// 상세 조회이력을 저장한다.  	
    gfn_InsertFPISDetailSearchPrintHistory(
        gubun			// 대상구분
        , menuId		// 메뉴 아이디 
        , searchNm		// 조회 대상명 
        , sMFPIS1		// 메인 PK
        , sMFPIS2		// 운송자
        , sMFPIS3		// 소기 
        , sMFPIS4		// 소기 
        , sMFPIS5		// 소기 
        , sMFPIS6		// 소기 
    );
}
