function Form_OnLoadCompleted(obj) {
    
    //gfn_Form_OnLoadCompletedCheck(obj);	//공통 폼 로딩 이벤트 처리(공통코드 조회되었는지 확인)
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";
	
	// 우선처음은 소방안전관리자 TAB이므로 직능을 소방안전관리자로 설정한다. ( 선임정보의 직능 )
	   tabtab3.tabpgTab3Page1.cbxcourse.Index = 0;
	   tabtab3.TabIndex = 0;
	   
		//ds_oJibu_Search.Copy("gds_oFireBonbuCode");		 				//소방본부 콤보세팅
		ds_oFireCd.Copy("gds_oFireCode");  				 				//소방서 콤보세팅	
		//ds_oJibu.Copy("gds_oFireBonbuCode");		 					//소방본부 콤보세팅
		//ds_oFireCenterCd.Copy("gds_oFireCenterCode");    				//소방센터 콤보세팅
	
	   gfn_SearchKCode(ds_oJibu_Search, "F", "전체");
	   gfn_SearchKCode(ds_chk100, "D", "전체");
	   gfn_SearchKCode(ds_Ajbizgubun, "D", "");
	   gfn_SearchKCode(ds_chk3, "B", "전체");
	   gfn_SearchKCode(ds_chk4, "B", " ");
	   gfn_SearchKCode(ds_KwanYN, "H", "선택");
	   gfn_SearchKCode(ds_CNTRWK_SPRVISOR_SE, "K", "선택");
	   gfn_SearchKCode(ds_CNTRWK_EVNC, "J", "선택");
	   gfn_SearchKCode(ds_MNGT_SE, "I", "선택");
	   
	   gfn_SearchCommonCode(ds_oBizCd, "BIZCD", "Y", "지정요망"); 				// 방관-용도코드
	   gfn_SearchKCode(ds_CNTRWK_SPRVISOR_SE_G, "K", "선택");
	   gfn_SearchKCode(ds_CNTRWK_EVNC_G, "J", "선택");
	   
	   gfn_SearchAllJibu(cbxJibu, "Y", "전체"); // 지부 코드 조회
	   
	   ds_oFireCd.Copy("gds_oFireCode");
	   
	   	// 본부 자동선택
    if (gds_oUserInfo.GetColumn(0, "PDEPTCD") > 1000) {		
		var PDEPTCD = gds_oUserInfo.GetColumn(0, "PDEPTCD");		
		// 경기북부인경우
//		if (PDEPTCD == "1141") {
//			PDEPTCD = "1081";
//		}
		var fireRow = cbxJibu.FindRow("JIBUCD", PDEPTCD);
		if (fireRow <> -1) {
			cbxJibu.Value = cbxJibu.GetColumn(fireRow,"CD");
			
			lfn_FilterFireStation(cbxJibu.Value);
		}
	} else {
		cbxJibu.Value = ds_oJibu.GetColumn(0,"CD");
		lfn_FilterFireStation(cbxJibu.Value);
	}
	
	// 소방서 디폴트 인덱스
	cbxStation.Index = 1;  
	edAJNM.SetFocus();
    lfn_Search_main(); 
}

