function btnToExcel_OnClick(obj)
{
	/*if(ds_List.rowcount == 0){
		alert("데이터가 없습니다.");
		return;
	}*/
	
	var strText = "선해임접수";
	
	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1115";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_PartCpnt.GetRowCount();
		parValue += " sTitle=" + quote(gdList.UserData);
	ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
	
	setExcelGridHeader(grdList,strText);	
}

