// 신규접수
function btnNewJubsu_OnClick(obj)
{
	// ========================================= //
	if(tabTab3.TabIndex == 0)
	{
		ds_iMaster.ClearData();
		ds_iMaster.AddRow();
	}
	else if(tabTab3.TabIndex == 1)
	{
		ds_iMaster.ClearData();
		ds_iMaster.AddRow();
		ds_iMasterTab2.ClearData();
		ds_iMasterTab2.AddRow();
	}
	else 
	{
		ds_iMaster.ClearData();
		ds_iMaster.AddRow();
		
	}
	// ========================================= //
	
	
	calAjdate.Enable = true;
	cbxAjstatus.Enable = true;
	cbxAjmothod.Enable =  true;
	edBILD_NM.Enable = true;
	
	calAjdate.Value = today();
	cbxAjstatus.Index = 1;
	cbxAjmothod.Index = 3;
	
		
	/*
	for (var i = 0; i < ds_oMaster.GetRowCount(); i++) { 
		if (sOldOrder < ToInteger(ds_oMaster.GetColumn(i, "AJMGNO"))) {
			sOldOrder = ToInteger(ds_oMaster.GetColumn(i, "AJMGNO"));
		} 
    }    
    edAJMGNO.Text = sOldOrder + 1;//회차
    */
    
	if (ADMINGUBUN == "SM" || ADMINGUBUN = "DM") {
		var parValue = " sJibu="+cbxJibu.value;
			parValue += " sGubun=customer";
			parValue += " sPagenm='frmappoint3030MFireManagerJubsuManagement'";
	
		var arrRtn = Dialog("appoint::frmcust3030.xml", parValue, 800, 700, "", -1, -1);
		
		if (length(arrRtn) > 0) {
		
			if(tabTab3.TabIndex == 0)
			{
				edbild_sn.Text = arrRtn[1];
				edbild_nm.Text = arrRtn[2];
				tabTab3.tabpgTab3Page1.edPARTCPNT_SN.Text = arrRtn[3];
				tabTab3.tabpgTab3Page1.cbxPartGubun.Value = arrRtn[4];		
				tabTab3.tabpgTab3Page1.edPartNM.Text = arrRtn[5];
				tabTab3.tabpgTab3Page1.edTLPHON_NO.Text = arrRtn[6];
				tabTab3.tabpgTab3Page1.calIHIDNUM.Text = arrRtn[7];
				tabTab3.tabpgTab3Page1.edO_NM.Text = arrRtn[8];
				tabTab3.tabpgTab3Page1.calO_IHIDNUM.Text = arrRtn[9];
				tabTab3.tabpgTab3Page1.edO_QUALFUSER_SN.Text = arrRtn[10];
				tabTab3.tabpgTab3Page1.edO_PERSONKEY.Text = arrRtn[11];
				tabTab3.tabpgTab3Page1.calO_APNT_DE_8.Text = replace(arrRtn[12],"-","");
				tabTab3.tabpgTab3Page1.edGNRL_FIRE_SAFT_AT.Text = arrRtn[13];
				tabTab3.tabpgTab3Page1.edTOT_MNGT_AUTH_NO.Text = arrRtn[14];
				tabTab3.tabpgTab3Page1.cbxEVNC.Value = arrRtn[15];
				tabTab3.tabpgTab3Page1.edFIRE_SAFT_ASSI_NO.Text = arrRtn[16];
				tabTab3.tabpgTab3Page1.edFIRE_SAFT_ASSI_NO.Text = arrRtn[17];
				
				tabtab3.tabpgTab3Page1.cbxCourse.Index = 0;
			}
			else if(tabTab3.TabIndex == 1)
			{
				edbild_sn.Text = arrRtn[1];
				edbild_nm.Text = arrRtn[2];
				inf_Search_SB(edBILD_SN.Text, edAJMGNO.Text);	// 소방안전관리자현황
				inf_Search_SBB(edBILD_SN.Text,edAJMGNO.Text);	// 소방안전보조자현황
				tabtab3.tabpgTab3Page2.cbxCourse.Index = 1;
			}
			else if(tabTab3.TabIndex == 2)
			{
				edbild_sn.Text = arrRtn[1];
				edbild_nm.Text = arrRtn[2];
				tabTab3.tabpgTab3Page3.edO_NM.Text = arrRtn[8];
				tabTab3.tabpgTab3Page3.calO_IHIDNUM.Text = arrRtn[9];
				tabTab3.tabpgTab3Page3.edO_QUALFUSER_SN.Text = arrRtn[10];
				tabTab3.tabpgTab3Page3.edO_PERSONKEY.Text = arrRtn[11];
				tabTab3.tabpgTab3Page3.calO_APNT_DE_8.Text = replace(arrRtn[12],"-","");
				
				tabtab3.tabpgTab3Page1.cbxCourse.Index = 2;
				ifn_Search_Const(arrRtn[1]);
			}
		}
	
	} else {
		gfn_Alert("소방청 정보는 보안등으로 인하여 회원담당자만 볼 수 있습니다.");
		return;
	}
}

// 저장
function lfn_Save(obj)
{	 
	/*
	 * 저장시 필수로 선택해야 하는 항목에 대한 조건처리
	*/
	
	if (gfn_isNull(ds_iMaster.GetColumn(0,"AJDATE")))
	{
		gfn_Alert("신청일자를 입력하세요!.");
		calAjdate.SetFocus();
		return false;
	}
	if(gfn_isNull(ds_iMaster.GetColumn(0,"AJMOTHOD")))
	{
		gfn_Alert("접수방법을 선택하세요!.");
		cbxAjmothod.SetFocus();
		return false;
	}
	if(gfn_isNull(ds_iMaster.GetColumn(0,"AJNM")))
	{
		gfn_Alert("신청인을 입력하세요!.");
		edAJNM.SetFocus();
		return false;
	}
	if(gfn_isNull(ds_iMaster.GetColumn(0,"AJHP")))
	{
		gfn_Alert("신청자의 연락처를 기입하세요!.");
		cbxJibu.SetFocus();
		return false;
	}
	if(gfn_isNull(ds_iMaster.GetColumn(0,"AJGTMGNO")))
	{
		gfn_Alert("신청지부를 선택하세요!.");
		cbxJibu.SetFocus();
		return false;
	}
			
	if(tabtab3.tabpgTab3Page1.cbxARSON_GUBUN.Value == "3")
	{
		if(tabtab3.tabpgTab3Page1.edEntrpsnm.Text == "" or tabtab3.tabpgTab3Page1.edEntrpsnm.Text == null)
		{
			gfn_Alert("선임구분이 감독적직위자의 경우 \n 업체대행 정보를 반드시 기입하여야 합니다!.");
			return false;
		}
	}
	
	
	var SqlSet = "";
	var oDms = "";
	var agment = "";
	SqlSet = "cust:insertCust3030Mfmanagement";
	oDms = "ds_iMaster=ds_iMaster:U";
		
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		  SqlSet	// insert
		, SqlSet	// update
		, ""		// delete
		, ""		// flag column 명
		, ""		// key sql id
		, 0			// key 증가 값
		, "" 
		, 0 
		, "N"		// 실행타입
	);  
	// 서버 호출 
	tit_callService(
		""
		,""
		,oDms	// inDs
		,""		// outDs
		,""		// args 
		,"lfn_AfterSaveTrainingOrder"
		,false
	);
}


// 접수시작
function fnSaveAction(statusnm)
{
	var SqlSet = "";
	var oDms = "";
	var agment = "";
	SqlSet = "cust:UpdateCust3030JupsuUpdate";
	oDms = "ds_iMaster=ds_iMaster:U";
		
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		  ""		// insert
		, SqlSet	// update
		, ""		// delete
		, ""		// flag column 명
		, ""		// key sql id
		, 0			// key 증가 값
		, "" 
		, 0 
		, "N"		// 실행타입
	);  
	// 서버 호출 
	tit_callService(
		""
		,""
		,oDms	// inDs
		,""		// outDs
		,"PSABUN=" + quote(gds_oUserInfo.GetColumn(0, "PSABUN"))  
		+ " STATUSNM="  + quote(statusnm) // 선임자이력순번
		,"lfn_AfterSaveTrainingOrder"
		,false
	);
}

