function ifn_serch_authority(bildno)
{
	tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3030Authority", false);
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oAuthority=ds_oAuthority"	// outDs
		,"BILD_SN=" + quote(bildno)  
		+ " AJMGNO="  + quote(edAJMGNO.Text) // 선임자이력순번
		,"lfn_AfterSearch"
		,false
	);
}

/*------------------------------------------------------------
 * 기능 : Transaction 서비스 호출 후 처리해야 할 내용
 * parameter : strSvcID - 서비스ID
 *             nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 *-------------------------------------------------------------*/
function lfn_AfterSaveTrainingOrder(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
   
    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    gdList_OnCellDblClick();
    lfn_Search(); // 교육일정관리 조회
}

function lfn_AfterSearchEducationPlace(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
	
	if (tabtab3.TabIndex = 0)
	{
		tabTab3_tabpgTab3Page1_cbxKwanYN_OnChanged();
		
		// 조회가 되었을때  선임자가 이미 선임되어 있는 경우 다른 관리권원을 선택못하도록 enable 처리
		if(ds_imaster.GetColumn(0, "I_NM") != '' or ds_imaster.GetColumn(0, "I_NM") != NULL)
		{
			tabtab3.tabpgTab3Page1.cbxMNGT_SEQ.Enable = false;
		}
		else {
			tabtab3.tabpgTab3Page1.cbxMNGT_SEQ.Enable = true;
		}
	} 
	else if(tabtab3.TabIndex = 1)
	{
		inf_Search_SB(edBILD_SN.Text, edAJMGNO.Text);
		inf_Search_SBB(edBILD_SN.Text, edAJMGNO.Text);
		
		// 조회가 되었을때  선임자가 이미 선임되어 있는 경우 다른 관리권원을 선택못하도록 enable 처리
		if(ds_imaster.GetColumn(0, "I_NM") != '' or ds_imaster.GetColumn(0, "I_NM") != NULL)
		{
			tabtab3.tabpgTab3Page2.cbxMNGT_SEQ.Enable = false;
		}
		else {
			tabtab3.tabpgTab3Page2.cbxMNGT_SEQ.Enable = true;
		}
		
	}
}

function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
}

function lfn_Search(AJMGNO) {

    ds_iMaster.ClearData(); // 데이타셋 초기화

	tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3030ManagerList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_iMaster=ds_oMaster"	// outDs
		,"AJMGNO="+ds_oMaster.GetColumn(ds_oMaster.GetCurrow(), "AJMGNO")+"" // args 
		,"lfn_AfterSearchEducationPlace"
		,false
	);
}

function lfn_Search_main() {

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust3030ManagerList", false);
	
	var Agg = "";
		agg = "";
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oMaster=ds_oMaster "	// outDs
        ,"" // args 
		,""
		,false
	);
}

function tabTab3_OnChanged(obj,nOldIndex,nNewindex)
{	
	// TAB을 변경할때마다 해당 TAB의 명칭에 맞춰서 선임정보의 직능 정보를 바꿔준다.
	// 첫번째, 두번째, 세번째의 따라 10: 소방안전관리자 , 20 : 소방안전관리보조자, 30 : 건설현장소방안전관리자
	if(nNewindex == 0)
	{	
		tabtab3.tabpgTab3Page1.cbxCourse.Value = 10;	
	}
	else if(nNewindex == 1)
	{
		tabtab3.tabpgTab3Page2.cbxCourse.Value = 20;
	}
	else {
		//tabtab3.tabpgTab3Page3.cbxCourse.Value = 30;	
	}
	
	var index = tabtab3.TabIndex;
	
	if(edajmgno.Text != "" and nOldIndex <> nNewindex)
	{
		alert("접수내역을 불러온 건은 탭이동을 할 수 없습니다.");
		gdList_OnCellDblClick();
	}
}

