	ds_imaster.SetColumn(0, "RCEPT_ROAD_NM",ds_oList.GetColumn(ds_oList.currow, "RCEPT_ROAD_NM"));
	ds_imaster.SetColumn(0, "RCEPT_MAIN_BDNBR",ds_oList.GetColumn(ds_oList.currow, "RCEPT_MAIN_BDNBR"));
	ds_imaster.SetColumn(0, "RCEPTSEC_BDNBR",ds_oList.GetColumn(ds_oList.currow, "RCEPTSEC_BDNBR"));
	ds_imaster.SetColumn(0, "RCEPT_BULD_NM",ds_oList.GetColumn(ds_oList.currow, "RCEPT_BULD_NM"));
	ds_imaster.SetColumn(0, "RCEPT_BULD_NM_ETC",ds_oList.GetColumn(ds_oList.currow, "RCEPT_BULD_NM_ETC"));
	ds_imaster.SetColumn(0, "REGISTER_ID",ds_oList.GetColumn(ds_oList.currow, "REGISTER_ID"));
}

function tabTab3_tabpgTab3Page1_btnSendSms_OnClick(obj)
{
	var ls_PERSONKEY = ""; // PERSONKEY 값 
	
	var ls_ManagerNm = tabTab3.tabpgTab3Page3.edi_nm.Value;	
	var ls_ResidentNo = trim(tabtab3.tabpgTab3Page3.edI_IHIDNUM.Value);
	var ls_PTel = tabtab3.tabpgTab3Page3.edI_HPNO.Value;
	if(ls_ManagerNm == "" || ls_ResidentNo == "" || length(ls_ResidentNo) < 6 ){
		gfn_alert("선임자명, 주민등록번호 입력 후 확인 가능합니다.");
		return;
	}
	// 이름, 주민번호, 핸드폰으로 식별키 검색 
	ls_PERSONKEY = gfn_SearchPERSONKEY(ls_ManagerNm, ls_ResidentNo, replace(ls_PTel,"-","")); // 실명인증 및 personkey 조회
	
	
		
	if(ls_PERSONKEY = ""){
		
		var sArg  = "sPNM=" + quote(trim(ls_ManagerNm));
		sArg += " sPIHIDNUM=" + quote(trim(ls_ResidentNo));
		
		// 개인식별키 없을 시 안전원정보확인 팝업에서 선임자 선택 해 오기 
		var arrRtnPre = Dialog("appoint::frmcust3030PPersonkeyInfoList.xml", sArg, 800, 402, "", -1, -1);
		
		if (length(arrRtnPre) > 0) {
			/*
			var rtnMsg = "";
			rtnMsg = "조회결과선택 -결과선택(Y), 해당없음 (N) : " +  arrRtnPre[0] ;
			rtnMsg += "\n" + "개인식별번호 : " +  arrRtnPre[1] ; 
			alert(rtnMsg);
			*/
			/*
			arrRtnPre[0] : 조회결과선택 -결과선택(Y), 해당없음 (N)
			arrRtnPre[1] :개인식별번호
			*/
			if(arrRtnPre[0] == "Y"){
				ls_PERSONKEY = arrRtnPre[1]; //개인식별키 담기 
			}		
		}
	}
	
	var sArg  = "sPNM=" + quote(trim(ls_ManagerNm));
		sArg += " sPIHIDNUM=" + quote(trim(ls_ResidentNo));
		sArg += " sPersonkey=" + quote(ls_PERSONKEY);
	var arrRtn = Dialog("appoint::frmcust3030PLicenseList.xml", sArg, 800, 402, "", -1, -1);
	
	
	if (length(arrRtn) > 0) {
		/*	
		arrArg[0] : 자격연동 -연계(Y), 직적입력 (N)
		arrArg[1] : 자격등급 
		arrArg[2] : 자격번호 
		arrArg[3] : 교육일자
		*/
		var rtnMsg = "";
		rtnMsg = "자격연동 -연계(Y), 직적입력 (N) : " +  arrRtn[0] ;
		rtnMsg += "\n" + "자격등급 : " +  arrRtn[1] ;
		rtnMsg += "\n" + "자격번호 : " +  arrRtn[2] ;
		rtnMsg += "\n" + "교육일자 : " +  arrRtn[3] ; 
		rtnMsg += "\n" + "선임자 자격 일련번호 : " +  arrRtn[5] ; 
		rtnMsg += "\n" + "PERSONKEY : " +  arrRtn[6] ; 
		alert(rtnMsg);
		
		tabtab3.tabpgTab3Page3.edIRGRADE.Text = arrRtn[1]; 					// 자격등급
		tabtab3.tabpgTab3Page3.edARSON_MANAGE_PKTBUK_NO.Text = arrRtn[2]; 	// 자격번호
		tabtab3.tabpgTab3Page3.edI_QUALFUSER_SN.Text = arrRtn[5];		   	// 자격자 일련번호
		tabtab3.tabpgTab3Page3.edI_PERSONKEY.Text = arrRtn[6];				// PERSONKEY
		
		
		
		
		tabtab3.tabpgTab3Page3.edZip.Text = arrRtn[7];							// 우편번호
		ds_imaster.SetColumn(0, "CTRD_CODE", 	arrRtn[8]);
		ds_imaster.SetColumn(0, "SIGNGU_CODE", 	arrRtn[9]);
		ds_imaster.SetColumn(0, "DONG_CODE", 	arrRtn[10]);
		ds_imaster.SetColumn(0, "LI_CODE", 		arrRtn[11]);
		ds_imaster.SetColumn(0, "MNTN_LNBR_AT", arrRtn[12]);
		ds_imaster.SetColumn(0, "MAIN_LNBR", 	arrRtn[13]);
		ds_imaster.SetColumn(0, "SEC_LNBR", 	arrRtn[14]);
		ds_imaster.SetColumn(0, "BASS_ADRES", 	arrRtn[15]);
		ds_imaster.SetColumn(0, "ETC_ADRES",	arrRtn[16]);
		ds_imaster.SetColumn(0, "ROAD_NM", 		arrRtn[17]);
		ds_imaster.SetColumn(0, "MAIN_BDNBR", 	arrRtn[18]);
		ds_imaster.SetColumn(0, "SEC_BDNBR", 	arrRtn[19]);
		ds_imaster.SetColumn(0, "BULD_NM", 		arrRtn[20]);
		ds_imaster.SetColumn(0, "BULD_NM_ETC", 	arrRtn[21]);
		ds_imaster.SetColumn(0, "I_ARSON_TYPE", arrRtn[22]);
		
		
		tabtab3.tabpgTab3Page3.edBASS_ADRES.Text = arrRtn[15];					// 기본주소
		tabtab3.tabpgTab3Page3.edETC_ADRES.Text = arrRtn[16];					// 상세주소	
		tabtab3.tabpgTab3Page3.calLAST_EDC_DE_08.Value = arrRtn[23];			// 교육일자 
	} 		
}

function tabTab3_tabpgTab3Page1_Button6_OnClick(obj)
{
	var sAddrGubun = 0;
	
	if (tabtab3.tabpgTab3Page1.cbxAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}
	
	// 지번주소/도로명주소 조회 팝업
	var arrAddrInfo = gfn_PostRoadListPopup(cbxJibu.Value										// 지부코드
											, sAddrGubun										// 구새주소 구분[0:구주소, 1:새주소]
											, tabtab3.tabpgTab3Page1.medEduPostZipCode.Value	// 구:지번 우편번호
											, tabtab3.tabpgTab3Page1.edEduPost1.Text			// 구:지번주소1
											, tabtab3.tabpgTab3Page1.edEduPost2.Text			// 구:지번주소2
											, ""
											, ""
											, ""
											, ""
											, ""
											, tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Value	// 새1:도로명 우편번호
											, tabtab3.tabpgTab3Page1.edEduRoadPost1.Text		// 새1:도로명주소11
											, tabtab3.tabpgTab3Page1.edEduRoadPost2.Text		// 새1:도로명주소12
											, ""
											, ""
											, ""
											, tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Value	// 새2:도로명 우편번호
											, tabtab3.tabpgTab3Page1.edEduRoadPost1.Text		// 새2:도로명주소21
											, tabtab3.tabpgTab3Page1.edEduRoadPost2.Text		// 새2:도로명주소22
											, ""
											, ""
											, ""
											); 
											

					
    if (length(arrAddrInfo) > 0) {
		
		if (arrAddrInfo[0] == 0) {
			
			/* 지번주소 조회 Tab */
			// 구주소
			tabtab3.tabpgTab3Page1.medEduPostZipCode.Value = arrAddrInfo[1];
			tabtab3.tabpgTab3Page1.edEduPost1.Text = arrAddrInfo[2];
			tabtab3.tabpgTab3Page1.edEduPost2.Text = arrAddrInfo[3];

					
			// 새주소
			if (!gfn_IsNull(arrAddrInfo[10])) {
				tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Value = arrAddrInfo[10];
			}
			
			tabtab3.tabpgTab3Page1.edEduRoadPost1.Text = arrAddrInfo[11];
			tabtab3.tabpgTab3Page1.edEduRoadPost2.Text = arrAddrInfo[12];
			
			ds_imaster.SetColumn(0, "CTRD_CODE_BASS"	,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "SIGNGU_CODE_BASS"	,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "DONG_CODE_BASS"	,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "LI_CODE_BASS"		,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "MNTN_LNBR_AT_BASS"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "MAIN_LNBR_BASS"	,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "SEC_LNBR_BASS"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "ROAD_NM_BASS"		,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "MAIN_BDNBR_BASS"	,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "SEC_BDNBR_BASS"	,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "BULD_NM_BASS"		,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "BULD_NM_ETC_BASS"	,arrAddrInfo[27]);	// 	건물 명 기타
			
			
			/////////////////////////////////////////////////////////////////////////////////////////////////////
			// -- 시도 및 관련주소코드 정보 가져오기
			tabtab3.tabpgTab3Page1.edSIDO_CODE_VER1.Text = arrAddInfo[16];
			/////////////////////////////////////////////////////////////////////////////////////////////////////
				
			// 주소 조회 후 구/새주소 구분
			if (arrAddrInfo[11] == "") {
				tabtab3.tabpgTab3Page1.cbxAddrGubun.Value = 0;	// 지번주소			
				tabtab3.tabpgTab3Page1_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun.Value);
			} else {
				tabtab3.tabpgTab3Page1.cbxAddrGubun.Value = 1;	// 도로명주소
				tabtab3.tabpgTab3Page1_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun.Value);
			}
		} else {
		
			/* 도로명주소 조회 Tab */
			tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Value = arrAddrInfo[10];	// 우편번호
			tabtab3.tabpgTab3Page1.edEduRoadPost1.Text = arrAddrInfo[11];			// 도로명주소1
			tabtab3.tabpgTab3Page1.edEduRoadPost2.Text = arrAddrInfo[12];			// 도로명주소2
			
			ds_imaster.SetColumn(0, "CTRD_CODE_BASS"	,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "SIGNGU_CODE_BASS"	,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "DONG_CODE_BASS"	,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "LI_CODE_BASS"		,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "MNTN_LNBR_AT_BASS"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "MAIN_LNBR_BASS"	,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "SEC_LNBR_BASS"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "ROAD_NM_BASS"		,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "MAIN_BDNBR_BASS"	,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "SEC_BDNBR_BASS"	,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "BULD_NM_BASS"		,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "BULD_NM_ETC_BASS"	,arrAddrInfo[27]);	// 	건물 명 기타
			
			// 주소 조회 후 구/새주소 구분
			tabtab3.tabpgTab3Page1.cbxAddrGubun.Value = 1;	// 도로명주소
			tabtab3_tabpgTab3Page1_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun.Value);
		}
    }	
}

/*청구지: 교육: 구/새주소 구분*/
function tabTab3_tabpgTab3Page1_cbxAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소
		tabtab3.tabpgTab3Page1.medEduPostZipCode.Visible = true;
		tabtab3.tabpgTab3Page1.edEduPost1.Visible = true;
		tabtab3.tabpgTab3Page1.edEduPost2.Visible = true;

		tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Visible = false;
		tabtab3.tabpgTab3Page1.edEduRoadPost1.Visible = false;
		tabtab3.tabpgTab3Page1.edEduRoadPost2.Visible = false;
	
	} else {			// 도로명주소
		tabtab3.tabpgTab3Page1.medEduRoadPostZipCode.Visible = true;
		tabtab3.tabpgTab3Page1.edEduRoadPost1.Visible = true;
		tabtab3.tabpgTab3Page1.edEduRoadPost2.Visible = true;
		
		tabtab3.tabpgTab3Page1.medEduPostZipCode.Visible = false;
		tabtab3.tabpgTab3Page1.edEduPost1.Visible = false;
		tabtab3.tabpgTab3Page1.edEduPost2.Visible = false;
	
	}
}


function tabTab3_tabpgTab3Page1_gdKwanList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	tabtab3.tabpgTab3Page1.edMGNT_SEQ.Text = ds_oAuthority.GetColumn(nRow, "MNGT_SEQ"); 	// 순번
	tabtab3.tabpgTab3Page1.cbxMGNT_USEYN.Value = ds_oAuthority.GetColumn(nRow, "USE_AT"); 	// 사용여부
	tabtab3.tabpgTab3Page1.edMGNT_NM.Text = ds_oAuthority.GetColumn(nRow, "MNGT_NM"); 		// 명칭
	tabtab3.tabpgTab3Page1.edMGNT_CEONM.Text = ds_oAuthority.GetColumn(nRow, "MNGT_CEONM"); 	// 대표자
	tabtab3.tabpgTab3Page1.edMGNT_TLPHON.Text = ds_oAuthority.GetColumn(nRow, "TLPHON"); 	// 전화번호
	tabtab3.tabpgTab3Page1.edUndernd_foor_qt.Text = ds_oAuthority.GetColumn(nRow, "UNDERND_FOOR_QT"); 	// 지하층
	tabtab3.tabpgTab3Page1.edTrrphnm_floor_qt.Text = ds_oAuthority.GetColumn(nRow, "TRRPHNM_FLOOR_QT"); // 지상층
	tabtab3.tabpgTab3Page1.edTorar.Text = ds_oAuthority.GetColumn(nRow, "TORAR"); 						// 연면적
	tabtab3.tabpgTab3Page1.edBOTTOM_AR.Text = ds_oAuthority.GetColumn(nRow, "BOTTOM_AR"); 				// 바닥면적
	tabtab3.tabpgTab3Page1.edDONG_QT.Text = ds_oAuthority.GetColumn(nRow, "DONG_QT"); 					// 동수	
	
	var chkyn = ds_oAuthority.GetColumn(nRow, "CHECKYN");	
	if(ds_oAuthority.GetRowCount() > 0)
	{		
		if(ds_oAuthority.GetColumn(nRow, "CHECKYN") == "N")
		{
			gfn_alert("해당 순번은 데이터 처리를 할 수 없습니다. \n 이미 처리된 순번이 존재합니다. 오른쪽 관리권원 표시 번호를 확인하세요!.");
			return false;
		}
	}
	
	
	tabtab3.tabpgTab3Page1.cbxMNGT_SEQ.value = tabtab3.tabpgTab3Page1.edMGNT_SEQ.Text;

}

