			ds_imaster.SetColumn(0, "DONG_CODE_ENTRPS"		,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "LI_CODE_ENTRPS"		,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "MNTN_LNBR_AT_ENTRPS"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "MAIN_LNBR_ENTRPS"		,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "SEC_LNBR_ENTRPS"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "ROAD_NM_ENTRPS"		,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "MAIN_BDNBR_ENTRPS"		,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "SEC_BDNBR_ENTRPS"		,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "BULD_NM_ENTRPS"		,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "BULD_NM_ETC_ENTRPS"	,arrAddrInfo[27]);	// 	건물 명 기타
				
			// 주소 조회 후 구/새주소 구분
			if (arrAddrInfo[11] == "") {
				tabtab3.tabpgTab3Page1.cbxAddrGubun_ENTRPS.Value = 0;	// 지번주소			
				tabtab3.tabpgTab3Page1_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun_ENTRPS.Value);
			} else {
				tabtab3.tabpgTab3Page1.cbxAddrGubun_ENTRPS.Value = 1;	// 도로명주소
				tabTab3_tabpgTab3Page1_cbxAddrGubun_ENTRPS_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun_ENTRPS.Value);
			}
		} else {
		
			/* 도로명주소 조회 Tab */
			tabtab3.tabpgTab3Page1.medEduRoadPostZipCode_ENTRPS.Value = arrAddrInfo[10];	// 우편번호
			tabtab3.tabpgTab3Page1.edEduRoadPost1_ENTRPS.Text = arrAddrInfo[11];			// 도로명주소1
			tabtab3.tabpgTab3Page1.edEduRoadPost2_ENTRPS.Text = arrAddrInfo[12];			// 도로명주소2
			
			ds_imaster.SetColumn(0, "CTRD_CODE_ENTRPS"		,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "SIGNGU_CODE_ENTRPS"	,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "DONG_CODE_ENTRPS"		,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "LI_CODE_ENTRPS"		,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "MNTN_LNBR_AT_ENTRPS"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "MAIN_LNBR_ENTRPS"		,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "MAIN_LNBR_ENTRPS"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "SEC_LNBR_ENTRPS"		,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "MAIN_BDNBR_ENTRPS"		,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "SEC_BDNBR_ENTRPS"		,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "BULD_NM_ENTRPS"		,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "BULD_NM_ETC_ENTRPS"	,arrAddrInfo[27]);	// 	건물 명 기타
			
			
			// 주소 조회 후 구/새주소 구분
			tabtab3.tabpgTab3Page1.cbxAddrGubun.Value = 1;	// 도로명주소
			tabtab3_tabpgTab3Page1_cbxAddrGubun_ENTRPS_OnChanged(obj, tabtab3.tabpgTab3Page1.cbxAddrGubun_ENTRPS.Value);
			
		}
    }
}

function tabTab3_tabpgTab3Page1_cbxAddrGubun_ENTRPS_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소
		tabtab3.tabpgTab3Page1.medEduPostZipCode_ENTRPS.Visible = true;
		tabtab3.tabpgTab3Page1.edEduPost1_ENTRPS.Visible = true;
		tabtab3.tabpgTab3Page1.edEduPost2_ENTRPS.Visible = true;

		tabtab3.tabpgTab3Page1.medEduRoadPostZipCode_ENTRPS.Visible = false;
		tabtab3.tabpgTab3Page1.edEduRoadPost1_ENTRPS.Visible = false;
		tabtab3.tabpgTab3Page1.edEduRoadPost2_ENTRPS.Visible = false;
	
	} else {			// 도로명주소
		tabtab3.tabpgTab3Page1.medEduRoadPostZipCode_ENTRPS.Visible = true;
		tabtab3.tabpgTab3Page1.edEduRoadPost1_ENTRPS.Visible = true;
		tabtab3.tabpgTab3Page1.edEduRoadPost2_ENTRPS.Visible = true;
		
		tabtab3.tabpgTab3Page1.medEduPostZipCode_ENTRPS.Visible = false;
		tabtab3.tabpgTab3Page1.edEduPost1_ENTRPS.Visible = false;
		tabtab3.tabpgTab3Page1.edEduPost2_ENTRPS.Visible = false;
	
	}	
}

// 소방 본부 선택시 본부 포함된 서센터 정보 필터링
function lfn_FilterFireStation(sFilterGubun) {	
	ds_oFireCd.UnFilter();
	//ds_oFireCenterCd.UnFilter();
	
	//  필터링
	var ls_FilterExpr = "";
    if (!gfn_IsNull(sFilterGubun)) {
        ls_FilterExpr += "CTCD='" + trim(sFilterGubun) + "'";
    }    
    ds_oFireCd.Filter(ls_FilterExpr);        
    
    if( ds_oFireCd.FindRow("DATA", cbxStation.Text) == -1) {
		cbxStation.Text = "";
    }
    
    if (ds_oFireCd.GetColumn(0,"DATA") == "전체") {
		ds_oFireCd.AddRow(0);
    } else {
		ds_oFireCd.InsertRow(0);
    }
	ds_oFireCd.SetColumn(0,"DATA","전체");
	ds_oFireCd.SetColumn(0,"CTCD",trim(sFilterGubun));    
	
    cbxStation.Index = 0; 
}

