function tabTab3_tabpgTab3Page1_btnDownLoad_Tab3_OnClick(obj)
{
	if (ds_oFileList.GetRowCount() < 1) {
        gfn_Alert("다운로드할 데이터가 존재하지 않습니다.");
        return;
    }

	var vRow = ds_oFileList.currow;
	 
	/*
	var ls_FileUploadPath = "/IMGDATA/ARSON/"+edfilePath.Text;  //  /IMGDATA/ARSON/2022/
    var ls_RealFileName = edfileNm.Text; // MANUAL_20130611163213796.pdf 
    //???? 실제 파일명, 저장명 필요할듯 
    var ls_SaveFileName = edfileNm.Text; //개인정보동의서 관리 사용자설명서.pdf
    */
    
	var ls_FileUploadPath = ds_oFileList.GetColumn(vRow,"AJFPATH");
    var ls_EFileName = ds_oFileList.GetColumn(vRow,"AJIFFILENM");  // 3B6236087A55435AB0CC91F792F66EBA.png
    var ls_KFileName = ds_oFileList.GetColumn(vRow,"AJFREALFILENM"); // 행정지원_선임연기1.png 
   
    /*
    //test
    ls_FileUploadPath = "/BBSDATA";
    ls_EFileName = "MANUAL_20130611163213796.pdf";
    ls_KFileName = "개인정보동의서 관리 사용자설명서.pdf";    
    */
    
    
    if (length(ls_EFileName) > 0) {

        fdAttachFile.Type = "SAVE";
        fdAttachFile.FileName = ls_KFileName;

        if (fdAttachFile.open()) {
            var arrRtn = tit_fileRead(
                hfAttachFile
                , ls_FileUploadPath
                , ls_EFileName
                , fdAttachFile.FilePath
                , fdAttachFile.FileName
            );
 
			if (gfn_isNull(arrRtn[0])) {
				gfn_Alert("다운로드에 실패했습니다.");
			} else {
				gfn_Alert(MSGI00060); //다운로드 완료 메시지
			}
        }
    }
	else {	
		gfn_Alert("다운로드할 파일이 없습니다.");		
	}  	
}

function tabTab3_tabpgTab3Page1_btnUpLoad_Tab3_OnClick(obj)
{
	if (gfn_isNull(ds_iMaster.GetColumn(0, "AJMGNO"))) {	
        gfn_Alert("접수자료가 없습니다.\n\n" +
                  "접수자료 조회 후 등록하세요!");
        return;	
	}
	if (!gfn_Confirm("첨부파일을 업로드 하시겠습니까?")) {
		return false;
	}
	
    fdAttachFile.Type = "OPEN";
    if (fdAttachFile.open()) {
    
        fiAttachFile = fdAttachFile.FilePath + "\\" + fdAttachFile.FileName;
        fiAttachFile.Open("rb");
        var ln_FileSize = fiAttachFile.GetLength();
        ln_FileSize = ln_FileSize / 1024; // KB로 변경
        
        if (ln_FileSize > 2048) {
            gfn_Alert("파일 사이즈가 2MB가 넘었습니다.\n파일 사이즈 : " + NumFormat(ln_FileSize) + "KB");
            return false;
        }
        lb_TaskFileUpload = true;
    }		
	ds_oFileUpload.ClearData();
	ds_oFileUpload.AddRow();	
	ds_oFileUpload.SetColumn(0, "AJMGNO", ds_iMaster.GetColumn(0, "AJMGNO")); //접수관리번호(접수번호)
	ds_oFileUpload.SetColumn(0, "AJFSEQ", ""); //화일순번
	ds_oFileUpload.SetColumn(0, "AJFGUBUN", ""); //첨부화일 구분
    //ds_oFileUpload.SetColumn(0, "AJFPATH", ls_TaskUploadPath+"/"+ ds_ioMaster.GetColumn(0, "AJMGNO")+"/");	// 경로    
    ds_oFileUpload.SetColumn(0, "AJFPATH", ls_TaskUploadPath+"/"+ ds_iMaster.GetColumn(0, "AJMGNO")+"/");		// 경로
    //ds_oFileUpload.SetColumn(0, "AJFREALFILENM", 'asdf.png' );	// 파일명    
    //ds_oFileUpload.SetColumn(0, "AJIFFILENM", fdAttachFile.FileName );	// 파일명    
    ds_oFileUpload.SetColumn(0, "AJIFFILENM", 'asdf1.txt' ); // 물리적 파일명   
    ds_oFileUpload.SetColumn(0, "AJFREALFILENM", fdAttachFile.FileName ); //사용자 파일명 
    ls_UploadTaskName = fdAttachFile.FileName ;	// 파일명
    
	/*
    ds_oFileUpload.SetColumn(0, "AJFPATH", ls_TaskUploadPath+"/"+ ds_ioTrainingJubsu.GetColumn(0, "TOMGNO")+"/");	// 경로
    ds_oFileUpload.SetColumn(0, "FILENM", substr(ds_ioTrainingJubsu.GetColumn(0, "JUBSUNO"),3,3)+"_TASK"+divTask.radTaskGubun.Value+".hwp");	// 파일명    
    ls_UploadTaskName = substr(ds_ioTrainingJubsu.GetColumn(0, "JUBSUNO"),3,3)+"_TASK"+divTask.radTaskGubun.Value+".hwp";	// 파일명
    */
    
    tit_clearActionInfo();
    tit_addSingleActionInfo("cust:insertCust30JubsuFile", "", 0, "");
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_oFileUpload=ds_oFileUpload"    // inDs
        ,""    // outDs
        ,""    // args
        ,"lfn_AfterTaskSave"
        ,false
        ,false
        ,true
    );	
}

