function lfnBtnClick(obj)
{
	// 저장 시 구분값으로 사용
	ds_iMaster.SetColumn(0,"STATUS_FLAG",obj.ID); 
	
	var statusnm = "";
	
    switch(obj.ID) {
		
        case "btnSave": //저장
				
			if (!gfn_Confirm("저장 하시겠습니까?")) {
				return;
			}
			// 상태가 신청, 완료일때는 제외 
			if(cbxAjstatus.Value == '1' || cbxAjstatus.Value == '5'){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			//저장 전 필수체크 
			if(fnCheckSave() = false) return ;
			//저장 호출 
			lfn_Save();
	
            break;
            
        case "btnJubsuStart": //접수시작
				
			if (!gfn_Confirm("접수시작 하시겠습니까?")) {
				return;
			}
			
			// 상태가 신청일때만 가능
			if(cbxAjstatus.Value <> '1' ){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			
			ds_iMaster.SetColumn(0,"AJSTATUS","2"); 	
			statusnm = "JPSTART";		
			//저장 호출 
			fnSaveAction(statusnm);
    
            break;
            
        case "btnJubsuEnd": //접수완료
				
			if (!gfn_Confirm("승인완료 하시겠습니까?")) {
				return;
			}
			/*
			// 상태체크 
			*/
			//저장 전 필수체크 
			if(fnCheckSave() = false) return ;	
			ds_iMaster.SetColumn(0,"AJSTATUS","3");	
			statusnm = "JPEND";
			//저장 호출 
			fnSaveAction(statusnm);
	
            break;
            
        case "btnJubsuCancle": //반려
			if (!gfn_Confirm("반려처리 하시겠습니까?")) {
				return;
			}
			
			if(edCancleMark.Text = "")
			{
				gfn_alert(" 반려시 반려사유를 만드시 기입하여야 합니다. ");
				return false;
			}
	
			/*
			// 상태가 신청, 완료일때는 제외 
			if(cbxAjstatus.Value == '1' || cbxAjstatus.Value == '5'){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			*/
			
			ds_iMaster.SetColumn(0,"AJSTATUS","4"); 	
			statusnm = "JPCANCLE";
			//저장 호출 
			fnSaveAction(statusnm);
			
            break;
    }	
}

