function tabTab3_tabpgTab3Page1_btn_KwanSave_OnClick(obj)
{
	if(!gfn_Confirm("관리권원 정보를 추가로 저장하시겠습니까?."))
	{
		return;
	}
	
	var SqlSet = "";
	var oDms = "";
	var agment = "";
	SqlSet = "cust:insertCust3030Authority";
	oDms = "ds_iMaster=ds_iMaster:U";
		
	tit_clearActionInfo();
	tit_addSaveActionInfo(
		  SqlSet	// insert
		, SqlSet	// update
		, ""		// delete
		, ""		// flag column 명
		, ""		// key sql id
		, 0			// key 증가 값
		, "" 
		, 0 
		, "N"		// 실행타입
	);  
	// 서버 호출 
	tit_callService(
		""
		,""
		,oDms	// inDs
		,""		// outDs
		,""		// args 
		,"lfn_AfterSaveAutority"
		,false
	);
}

function lfn_AfterSaveAutority(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }    
}

