			// -- 시도 및 관련주소코드 정보 가져오기
			tabtab3.tabpgTab3Page2.edSIDO_CODE_VER1.Text = arrAddInfo[16];
			/////////////////////////////////////////////////////////////////////////////////////////////////////
				
			// 주소 조회 후 구/새주소 구분
			if (arrAddrInfo[11] == "") {
				tabtab3.tabpgTab3Page2.cbxAddrGubun.Value = 0;	// 지번주소			
				tabtab3.tabpgTab3Page2_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page2.cbxAddrGubun.Value);
			} else {
				tabtab3.tabpgTab3Page2.cbxAddrGubun.Value = 1;	// 도로명주소
				tabtab3.tabpgTab3Page2_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page2.cbxAddrGubun.Value);
			}
		} else {
		
			/* 도로명주소 조회 Tab */
			tabtab3.tabpgTab3Page2.medEduRoadPostZipCode.Value = arrAddrInfo[10];	// 우편번호
			tabtab3.tabpgTab3Page2.edEduRoadPost1.Text = arrAddrInfo[11];			// 도로명주소1
			tabtab3.tabpgTab3Page2.edEduRoadPost2.Text = arrAddrInfo[12];			// 도로명주소2
			
			ds_imaster.SetColumn(0, "CTRD_CODE_BASS"	,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "SIGNGU_CODE_BASS"	,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "DONG_CODE_BASS"	,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "LI_CODE_BASS"		,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "MNTN_LNBR_AT_BASS"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "MAIN_LNBR_BASS"	,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "SEC_LNBR_BASS"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "ROAD_NM_BASS"		,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "MAIN_BDNBR_BASS"	,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "SEC_BDNBR_BASS"	,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "BULD_NM_BASS"		,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "BULD_NM_ETC_BASS"	,arrAddrInfo[27]);	// 	건물 명 기타
			ds_imaster.SetColumn(0, "BULD_NM_ETC_BASS"	,arrAddrInfo[27]);	// 	건물 명 기타
			
			// 주소 조회 후 구/새주소 구분
			tabtab3.tabpgTab3Page2.cbxAddrGubun.Value = 1;	// 도로명주소
			tabtab3_tabpgTab3Page2_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page2.cbxAddrGubun.Value);
		}
    }	
}

/*청구지: 교육: 구/새주소 구분*/
function tabTab3_tabpgTab3Page2_cbxAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소
		tabtab3.tabpgTab3Page2.medEduPostZipCode.Visible = true;
		tabtab3.tabpgTab3Page2.edEduPost1.Visible = true;
		tabtab3.tabpgTab3Page2.edEduPost2.Visible = true;

		tabtab3.tabpgTab3Page2.medEduRoadPostZipCode.Visible = false;
		tabtab3.tabpgTab3Page2.edEduRoadPost1.Visible = false;
		tabtab3.tabpgTab3Page2.edEduRoadPost2.Visible = false;
	
	} else {			// 도로명주소
		tabtab3.tabpgTab3Page2.medEduRoadPostZipCode.Visible = true;
		tabtab3.tabpgTab3Page2.edEduRoadPost1.Visible = true;
		tabtab3.tabpgTab3Page2.edEduRoadPost2.Visible = true;
		
		tabtab3.tabpgTab3Page2.medEduPostZipCode.Visible = false;
		tabtab3.tabpgTab3Page2.edEduPost1.Visible = false;
		tabtab3.tabpgTab3Page2.edEduPost2.Visible = false;
	
	}
}


function tabTab3_tabpgTab3Page3_Button6_OnClick(obj)
{
	var sAddrGubun = 0;
	
	if (tabtab3.tabpgTab3Page3.cbxAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}
	
	// 지번주소/도로명주소 조회 팝업
	var arrAddrInfo = gfn_PostRoadListPopup(cbxJibu.Value										// 지부코드
											, sAddrGubun										// 구새주소 구분[0:구주소, 1:새주소]
											, tabtab3.tabpgTab3Page3.medEduPostZipCode.Value	// 구:지번 우편번호
											, tabtab3.tabpgTab3Page3.edEduPost1.Text			// 구:지번주소1
											, tabtab3.tabpgTab3Page3.edEduPost2.Text			// 구:지번주소2
											, ""
											, ""
											, ""
											, ""
											, ""
											, tabtab3.tabpgTab3Page3.medEduRoadPostZipCode.Value	// 새1:도로명 우편번호
											, tabtab3.tabpgTab3Page3.edEduRoadPost1.Text		// 새1:도로명주소11
											, tabtab3.tabpgTab3Page3.edEduRoadPost2.Text		// 새1:도로명주소12
											, ""
											, ""
											, ""
											, tabtab3.tabpgTab3Page3.medEduRoadPostZipCode.Value	// 새2:도로명 우편번호
											, tabtab3.tabpgTab3Page3.edEduRoadPost1.Text		// 새2:도로명주소21
											, tabtab3.tabpgTab3Page3.edEduRoadPost2.Text		// 새2:도로명주소22
											, ""
											, ""
											, ""
											); 
											

					
    if (length(arrAddrInfo) > 0) {
		
		if (arrAddrInfo[0] == 0) {
			
			/* 지번주소 조회 Tab */
			// 구주소
			tabtab3.tabpgTab3Page3.medEduPostZipCode.Value = arrAddrInfo[1];
			tabtab3.tabpgTab3Page3.edEduPost1.Text = arrAddrInfo[2];
			tabtab3.tabpgTab3Page3.edEduPost2.Text = arrAddrInfo[3];

					
			// 새주소
			if (!gfn_IsNull(arrAddrInfo[10])) {
				tabtab3.tabpgTab3Page3.medEduRoadPostZipCode.Value = arrAddrInfo[10];
			}
			
			tabtab3.tabpgTab3Page3.edEduRoadPost1.Text = arrAddrInfo[11];
			tabtab3.tabpgTab3Page3.edEduRoadPost2.Text = arrAddrInfo[12];
			
			ds_imaster.SetColumn(0, "RCEPT_SIDO"			,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "RCEPT_SIGNGU"			,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "RCEPT_DONG"			,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "RCEPT_RI"				,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "RCEPT_MNTN_LNBR_AT"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "RCEPT_MAIN_LNBR"		,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "RCEPT_SEC_LNBR"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "RCEPT_ROAD_NM"			,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "RCEPT_MAIN_BDNBR"		,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "RCEPTSEC_BDNBR"		,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "RCEPT_BULD_NM"			,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "RCEPT_BULD_NM_ETC"		,arrAddrInfo[27]);	// 	건물 명 기타	
			// 주소 조회 후 구/새주소 구분
			if (arrAddrInfo[11] == "") {
				tabtab3.tabpgTab3Page3.cbxAddrGubun.Value = 0;	// 지번주소			
				tabtab3.tabpgTab3Page3_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page3.cbxAddrGubun.Value);
			} else {
				tabtab3.tabpgTab3Page3.cbxAddrGubun.Value = 1;	// 도로명주소
				tabtab3.tabpgTab3Page3_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page3.cbxAddrGubun.Value);
			}
		} else {
		
			/* 도로명주소 조회 Tab */
			tabtab3.tabpgTab3Page3.medEduRoadPostZipCode.Value = arrAddrInfo[10];	// 우편번호
			tabtab3.tabpgTab3Page3.edEduRoadPost1.Text = arrAddrInfo[11];			// 도로명주소1
			tabtab3.tabpgTab3Page3.edEduRoadPost2.Text = arrAddrInfo[12];			// 도로명주소2
			
			ds_imaster.SetColumn(0, "RCEPT_SIDO"			,arrAddrInfo[16]);	// 	시도코드
			ds_imaster.SetColumn(0, "RCEPT_SIGNGU"			,arrAddrInfo[17]);	// 	시군구코드
			ds_imaster.SetColumn(0, "RCEPT_DONG"			,arrAddrInfo[18]);	// 	동 코드
			ds_imaster.SetColumn(0, "RCEPT_RI"				,arrAddrInfo[19]);	// 	리 코드
			ds_imaster.SetColumn(0, "RCEPT_MNTN_LNBR_AT"	,arrAddrInfo[20]);	// 	산 번지 여부
			ds_imaster.SetColumn(0, "RCEPT_MAIN_LNBR"		,arrAddrInfo[21]);	// 	본 번지
			ds_imaster.SetColumn(0, "RCEPT_SEC_LNBR"		,arrAddrInfo[22]);	// 	부 번지
			ds_imaster.SetColumn(0, "RCEPT_ROAD_NM"			,arrAddrInfo[23]);	// 	도로 명
			ds_imaster.SetColumn(0, "RCEPT_MAIN_BDNBR"		,arrAddrInfo[24]);	// 	본 건물 번호
			ds_imaster.SetColumn(0, "RCEPTSEC_BDNBR"		,arrAddrInfo[25]);	// 	부 건물 번호
			ds_imaster.SetColumn(0, "RCEPT_BULD_NM"			,arrAddrInfo[26]);	// 	건물 명
			ds_imaster.SetColumn(0, "RCEPT_BULD_NM_ETC"		,arrAddrInfo[27]);	// 	건물 명 기타
			
			// 주소 조회 후 구/새주소 구분
			tabtab3.tabpgTab3Page3.cbxAddrGubun.Value = 1;	// 도로명주소
			tabtab3_tabpgTab3Page3_cbxAddrGubun_OnChanged(obj, tabtab3.tabpgTab3Page3.cbxAddrGubun.Value);
		}
    }	
}

