		tabtab3.tabpgTab3Page3.medEduRoadPostZipCode.Visible = true;
		tabtab3.tabpgTab3Page3.edEduRoadPost1.Visible = true;
		tabtab3.tabpgTab3Page3.edEduRoadPost2.Visible = true;
		
		tabtab3.tabpgTab3Page3.medEduPostZipCode.Visible = false;
		tabtab3.tabpgTab3Page3.edEduPost1.Visible = false;
		tabtab3.tabpgTab3Page3.edEduPost2.Visible = false;
	
	}
}

