/*청구지: 교육: 구/새주소 구분*/
function tabTab3_tabpgTab3Page3_cbxAddrGubun_Bass_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == 0) {	// 지번주소
		tabtab3.tabpgTab3Page3.medEduPostZipCode_Bass.Visible = true;
		tabtab3.tabpgTab3Page3.edEduPost1_Bass.Visible = true;
		tabtab3.tabpgTab3Page3.edEduPost2_Bass.Visible = true;

		tabtab3.tabpgTab3Page3.medEduRoadPostZipCode_Bass.Visible = false;
		tabtab3.tabpgTab3Page3.edEduRoadPost1_Bass.Visible = false;
		tabtab3.tabpgTab3Page3.edEduRoadPost2_Bass.Visible = false;
	
	} else {			// 도로명주소
		tabtab3.tabpgTab3Page3.medEduRoadPostZipCode_Bass.Visible = true;
		tabtab3.tabpgTab3Page3.edEduRoadPost1_Bass.Visible = true;
		tabtab3.tabpgTab3Page3.edEduRoadPost2_Bass.Visible = true;
		
		tabtab3.tabpgTab3Page3.medEduPostZipCode_Bass.Visible = false;
		tabtab3.tabpgTab3Page3.edEduPost1_Bass.Visible = false;
		tabtab3.tabpgTab3Page3.edEduPost2_Bass.Visible = false;
	
	}
}
