/*
 * 기능 : 상태에 따른 버튼 활성/비활성	
*/
function fnBtnEnable(status){
	// 비활성 후 
	btnNewJubsu.Enable = false;
	btnSave.Enable = false;
	btnJubsuStart.Enable = false;
	btnJubsuEnd.Enable = false;
	btnJubsuCancle.Enable = false;
	
	// 화면 비활성 /활성
	cbxPartGubun.Enable = true;
	edPartNM.Enable = true;
	calIHIDNUM.Enable = true;
	edTLPHON_NO.Enable = true;
	edI_NM.Enable = true;
	edI_IHIDNUM.Enable = true;
	edI_HPNO.Enable = true;
	edEmail.Enable = true;
	btnSearchZipCode.Enable = true;
	edBookAddr2.Enable = true;
	rdETC_MATTER.Enable = true;
	rdAPNT_PSTPONE_CODE.Enable = true;
	edAPNT_PSTPONE_RESN.Enable = true;
	rdAPNT_PSTPONE_CODE_OnChanged();
	btnCheck.Enable = true;
	
	
	/* 1: 신청, 2: 접수시작, 3: 검토완료, 4: 저장완료, 5: 완료*/	
	btnNewJubsu.Enable = true;
	if(status == ''){
		btnSave.Enable = true;		
	}
	if(status == '1'){
		btnJubsuStart.Enable = true;	
			
		// 화면 비활성 /활성
		cbxPartGubun.Enable = false;
		edPartNM.Enable = false;
		calIHIDNUM.Enable = false;
		edTLPHON_NO.Enable = false;
		edI_NM.Enable = false;
		edI_IHIDNUM.Enable = false;
		edI_HPNO.Enable = false;
		edEmail.Enable = false;
		btnSearchZipCode.Enable = false;
		edBookAddr2.Enable = false;
		rdETC_MATTER.Enable = false;
		rdAPNT_PSTPONE_CODE.Enable = false;
		edAPNT_PSTPONE_RESN.Enable = false;
		btnCheck.Enable = false;
		
	}
	if(status == '2'){
		btnSave.Enable = true;			
		if (!gfn_isNull(ds_ioMaster.GetColumn(0,"AJMGNO"))) {	
			btnJubsuEnd.Enable = true;	
			btnJubsuCancle.Enable = true;	
		}
	}
	if(status == '3'){
		btnSave.Enable = true;	
		btnJubsuEnd.Enable = true;	
		btnJubsuCancle.Enable = true;	
	}
	if(status == '4'){
		btnSave.Enable = true;	
		btnJubsuEnd.Enable = true;	
		btnJubsuCancle.Enable = true;	
	}
	if(status == '5'){
		// 화면 비활성 /활성
		cbxPartGubun.Enable = false;
		edPartNM.Enable = false;
		calIHIDNUM.Enable = false;
		edTLPHON_NO.Enable = false;
		edI_NM.Enable = false;
		edI_IHIDNUM.Enable = false;
		edI_HPNO.Enable = false;
		edEmail.Enable = false;
		btnSearchZipCode.Enable = false;
		edBookAddr2.Enable = false;
		rdETC_MATTER.Enable = false;
		rdAPNT_PSTPONE_CODE.Enable = false;
		edAPNT_PSTPONE_RESN.Enable = false;
		btnCheck.Enable = false;
	}
	

}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    ds_oJubsuList.ClearData(); // 데이타셋 초기화
	
	var vSdate = "";
	var vEdate = "";
	if(cbDateGubun == '1'){ //1일 
		vSdate = left(getDate(), 8);
		vEdate = left(getDate(), 8);
	}else if(cbDateGubun == '2'){ //7일
		vSdate = addDate(left(getDate(), 8), -7);
		vEdate = left(getDate(), 8);
	}else if(cbDateGubun == '3'){ // 한달
		vSdate = addDate(left(getDate(), 8), -30);
		vEdate = left(getDate(), 8);
	}  
		
	tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:selectcust3040JubsuList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oJubsuList=ds_oJubsuList"	// outDs
		/*
		,"AJGTMGNO=" + quote(cbxJibu.Value) // 신청지부 
            + " FSTN_CNTER_ID=" + quote(cbxKS.Value) // 소속관서 (서 센터 ID)
            + " AJSTATUS=" + quote(cbStatus.value)  // (최종)상태
            + " AJDATESTART=" + quote(vSdate)	 
            + " AJDATEEND=" + quote(vEdate)	// args */
		,"AJGTMGNO=" + quote(cbxJibu.Value)
            + " FSTN_CNTER_ID=" + quote(cbxKS.Value)
            + " AJSTATUS=" + quote(cbStatus.value)
            + " AJDATESTART=" + quote(vSdate)
            + " AJDATEEND=" + quote(vEdate)
		,"lfn_AfterSearchList"
		,false
	);
}

 
/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.    
}


/**
 * 엑셀 출력
 */
function btnToExcel_OnClick(obj)
{	
    if (ds_oJubsuList.GetRowCount() < 1) {
        gfn_Alert("출력할 데이터가 존재하지 않습니다.");
        return;
    }

	// 개인정보 다운로드 사유 등록
	var parValue = "sCode=1403";	// COMMON.PERSONALDOWNLOADH
		parValue += " sRowCnt=" + ds_oJubsuList.GetRowCount();
		parValue += " sTitle=" + quote(gdList.UserData);
	var ls_IsRemark = Dialog("COM::frmCOM2100PDownloadRemark.xml", parValue, "", "", "", -1, -1);	

	if (ls_IsRemark == "" || ls_IsRemark == null || ls_IsRemark == "FAIL") {
		return false;
	}
	lfn_Excel(obj);
}

/*
 * 조회 그리드 선택 시 
*/
function gdList_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	ds_oJubsuList_OnRowPosChanged(ds_oJubsuList,nRow,nRow);
	
}

/*
 * 조회 dataset 줄 선택 시 
*/
function ds_oJubsuList_OnRowPosChanged(obj,nOldRow,nRow)
{ 
    ds_ioMaster.ClearData(); // 데이타셋 초기화
    ds_oFileList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:selectCust3040FireManagerPstpone", false);
	tit_addCsvSearchActionInfo("cust:selectCust3030JubsuFile", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_ioMaster=ds_ioMaster ds_oFileList=ds_oFileList"	// outDs
		,"AJMGNO=" + quote(ds_oJubsuList.GetColumn(nRow,"AJMGNO")) //선택한 접수관리번호  
		,"lfn_AfterJubsuNo"
		,false
	);  
	
}

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterJubsuNo(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    // 조회 후 상태에 따른 버튼 활성/비활성 
	fnBtnEnable(cbxAjstatus.Value);
		
	//조회 후 값 셋팅 
	if(ds_ioMaster.GetColumn(0,"APYEXM_RCEPT_NO") <> ""){ //시험접수번호 값이 있으면 
		cbTEgubun.Value = 'E';
	}
	if(ds_ioMaster.GetColumn(0,"EDC_RCEPT_NO") <> ""){ //강습접수번호 값이 있으면 
		cbTEgubun.Value = 'T';
	}
}

function lfnBtnClick(obj){
 
	// 저장 시 구분값으로 사용
	ds_ioMaster.SetColumn(0,"STATUS_FLAG",obj.ID); 
		
    switch(obj.ID) {
		
        case "btnNewJubsu": //신규접수
			lfnNewJubsu();
            break;

        case "btnSave": //저장
				
			if (!gfn_Confirm("저장 하시겠습니까?")) {
				return;
			}
			// 상태가 신청, 완료일때는 제외 
			if(cbxAjstatus.Value == '1' || cbxAjstatus.Value == '5'){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			//저장 전 필수체크 
			if(fnCheckSave(obj.ID) = false) return ;
			//저장 호출 
			fnSaveAction();
	
            break;
            
        case "btnJubsuStart": //접수시작
				
			if (!gfn_Confirm("접수시작 하시겠습니까?")) {
				return;
			}
			
			// 상태가 신청일때만 가능
			if(cbxAjstatus.Value <> '1' ){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			
			ds_ioMaster.SetColumn(0,"AJSTATUS","2"); 			
			//저장 호출 
			fnSaveAction();
    
            break;
            
        case "btnJubsuEnd": //접수완료
				
			if (!gfn_Confirm("접수완료 하시겠습니까?")) {
				return;
			}
			/*
			// 상태체크 
			*/
			//저장 전 필수체크 
			if(fnCheckSave(obj.ID) = false) return ;	
			ds_ioMaster.SetColumn(0,"AJSTATUS","5"); 			
			//저장 호출 
			fnSaveAction();
	
            break;
            
        case "btnJubsuCancle": //반려
			if (!gfn_Confirm("반려처리 하시겠습니까?")) {
				return;
			}
			/*
			// 상태가 신청, 완료일때는 제외 
			if(cbxAjstatus.Value == '1' || cbxAjstatus.Value == '5'){
				gfn_Alert("신청 상태를 확인해주십시오.");
				return false;
			}
			*/
			
			ds_ioMaster.SetColumn(0,"AJSTATUS","1"); 			
			//저장 호출 
			fnSaveAction();
			
            break;
    }
}

/*
 * 기능 : 저장 전 필수체크 
 */	
function fnCheckSave(arg)
{ 
	// 필수체크
	if (gfn_isNull(ds_ioMaster.GetColumn(0,"BILD_SN"))) {	
		gfn_Alert("대상물이 입력되지 않았습니다.");	
		return false;	
	}
	
	if(arg == "btnSave"){
		return true;
	}
	
	//접수완료(승인)일때만 체크 
	if (gfn_isNull(ds_ioMaster.GetColumn(0,"PARTCPNT_CODE")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"PARTCPNT")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"IHIDNUM")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"TLPHON_NO")) ) {	
		gfn_Alert("관계인정보가 입력되지 않았습니다.\n(관계인구분,성명,생년월일,연락처)");	
		edPartNM.SetFocus();
		return false;	
	}
	if (gfn_isNull(ds_ioMaster.GetColumn(0,"I_NM")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"I_IHIDNUM")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"I_HPNO")) ||
		gfn_isNull(ds_ioMaster.GetColumn(0,"ZIP"))||
		gfn_isNull(ds_ioMaster.GetColumn(0,"ETC_MATTER"))||
		gfn_isNull(ds_ioMaster.GetColumn(0,"APNT_PSTPONE_CODE")) ) {	
		gfn_Alert("선임자정보가 입력되지 않았습니다.\n(성명,주민번호,휴대폰,주소,선임분야,선임사유)");	
		edI_NM.SetFocus();
		return false;	
	}
	if (gfn_isNull(cbTEgubun.Value)) {	
		gfn_Alert(" 안전원 보유정보 확인을 해주십시오.");	
		btnCheck.SetFocus();
		return false;	
	}
	if (gfn_isNull(ds_ioMaster.GetColumn(0,"I_PERSONKEY")) ) {	
		gfn_Alert("선임자정보의 PERSONKEY가 없습니다.(필수확인) ");	
		edI_NM.SetFocus();
		return false;	
	}
	
	if(ds_ioMaster.GetColumn(0,"APNT_PSTPONE_CODE") == "100000740" &&
	   gfn_isNull(ds_ioMaster.GetColumn(0,"APNT_PSTPONE_RESN")) ){ //기타 
		gfn_Alert("선임사유가 기타이면 내용을 입력하세요");	
		rdAPNT_PSTPONE_CODE.SetFocus();
		return false;	
	}
	
	//ds_ioMaster setting
    ds_ioMaster.SetColumn(0,"AJNM",edI_NM.Value); //신청인
    ds_ioMaster.SetColumn(0,"AJHP",edI_HPNO.Value); //신청자HP
    // 2급,3급이면  - 1:소방안전관리자, 보조자이면 - 2:소방안전관리보조자	
    if (ds_ioMaster.GetColumn(0,"ETC_MATTER") == "1") ds_ioMaster.SetColumn(0,"AJCOURSECD","1"); 
    if (ds_ioMaster.GetColumn(0,"ETC_MATTER") == "2") ds_ioMaster.SetColumn(0,"AJCOURSECD","1"); 
    if (ds_ioMaster.GetColumn(0,"ETC_MATTER") == "3") ds_ioMaster.SetColumn(0,"AJCOURSECD","2"); 
    
    return true;
    
}
 
/*
 * 기능 : 신규 
 */
function lfnNewJubsu()
{   
	if (ADMINGUBUN <> "SM" && ADMINGUBUN <> "DM") {
		gfn_Alert("소방청 정보는 보안등으로 인하여 회원담당자만 볼 수 있습니다.");
		return;
	}

	var parValue = " sJibu="+cbxJibu.value;
		parValue += " sGubun=customer";
		parValue += " sPagenm='frmcust3030MFireManagerJubsuManagement'";
	
	var arrRtn = Dialog("appoint::frmcust3030.xml", parValue, 800, 700, "", -1, -1);
	
    if (length(arrRtn) > 0) {
    
		//초기화 
		ds_ioMaster.ClearData();
		ds_ioMaster.AddRow();
		ds_oFileList.ClearData();
		
		// ds_ioMaster setting 
		cbxAjstatus.Value = "2"; //2:접수시작(최종)상태 (Kgubun=B)
		cbxAjmothod.Value = "3"; //2:내방    신청방법 (Kgubun=E) 
		calAjdate.Value = left(GetDate(), 8); //신청일자(접수일자)    
		ds_ioMaster.SetColumn(0,"AJBIZGUBUN","3"); //3:연기 신청구분 (Kgubun=D)
		
		//대상물정보, 접수상태정보
		edBILD_SN.Text = arrRtn[1]; // 대상물관리번호 
		edBILD_NM.Text = arrRtn[2];	// 대상물명 
		
		//관계인정보
		edPARTCPNT_SN.Value = arrRtn[3]; // 관계자 순번 
		cbxPartGubun.Value = arrRtn[4]; // 관계자 코드
		edPartNM.Value = arrRtn[5]; // 관계자 성명
		edTLPHON_NO.Value = arrRtn[6]; // 관계자 전화번호
		calIHIDNUM.Value = arrRtn[7]; // 관계자 생년월일
		
		if(arrRtn[4].length == 0){
			cbxPartGubun.Value = "0590007"; // 관계자 코드
		}	
		
		edI_NM.SetFocus();
		fnBtnEnable(cbxAjstatus.Value);
    }
}

 
/*
 * 기능 : 안전원정보확인 팝업 호출 
 */
function btnCheck_OnClick(obj)
{
	
	var ls_PERSONKEY = ""; // PERSONKEY 값 
	
	var ls_ManagerNm = edI_NM.Value;	
	var ls_ResidentNo = trim(edI_IHIDNUM.Value);
	var ls_PTel = edI_HPNO.Value;
	if(ls_ManagerNm == "" || ls_ResidentNo == "" || length(ls_ResidentNo) < 6 ){
		gfn_alert("선임자명, 주민등록번호 입력 후 확인 가능합니다.");
		return;
	}
	// 이름, 주민번호, 핸드폰으로 식별키 검색 
	ls_PERSONKEY = gfn_SearchPERSONKEY(ls_ManagerNm, ls_ResidentNo, replace(ls_PTel,"-","")); // 실명인증 및 personkey 조회
	//trace(" gfn_SearchPERSONKEY 후 ls_PERSONKEY : " + ls_PERSONKEY);
	
		
	if(ls_PERSONKEY = ""){
		
		var sArg  = "sPNM=" + quote(trim(ls_ManagerNm));
		sArg += " sPIHIDNUM=" + quote(trim(ls_ResidentNo));
		
