function fnSaveAction()
{
    tit_clearActionInfo();
	tit_addSingleActionInfo("cust:insertCust3040FireManagerPstpone", "", 0, "");
    // 서버 호출
    tit_callService(
        ""
        ,""
        ,"ds_ioMaster=ds_ioMaster"/// inDs
        ,"ds_oMasterResult=ds_oMasterResult"	// outDs
		,""
        ,"lfn_AfterSave"
        ,false
    );
}

function lfn_AfterSave(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SaveMessage(); // 저장 메세지를 처리한다.
    
    trace("ds_oMasterResult : " + ds_oMasterResult.SaveXML());
    		
	var msg = "";             		   
	msg += "저장되었습니다.\n\n"; 
	msg += "접수번호 :  " + ds_oMasterResult.GetColumn(0, "O_AJMGNO");   				  
	gfn_Alert(msg);
	
    lfn_Search();
}

