#include "LIB::COMMON.js"; // 공통업무 관련



/* 공통 필수 시작 */

function Form_OnLoadCompleted(obj) {

    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리



    // 폼 환경설정

    ls_SystemGubun = gs_SystemGubun;

    ls_SystemMenuId = gs_SystemMenuId;

    var ls_FormId = split(sFormUrl, "::");

    ls_FormId = split(ls_FormId[1], ".");

    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")"; 
	//var url = "https://intrawork.kfsi.or.kr:444/groupware/user/UserMinwonMonitList.do?groupUserId=20151201&gtmgNo=0000&adminYn=Y";
	// 정보지원과, 고객지원과 만 관리자 권한 부여 
	var vAdmin = "";
	//trace(gds_CtiInfo.GetColumn(0, "GTMGNO"));
	if( gds_CtiInfo.GetColumn(0, "GTMGNO") == "0243" || gds_CtiInfo.GetColumn(0, "GTMGNO") == "0272" ){
		vAdmin = "&adminYn=Y";
	}
	var url = "https://intrawork.kfsi.or.kr:444/groupware/user/UserMinwonMonitList.do?gtmgNo=" + gds_CtiInfo.GetColumn(0, "GTMGNO") + vAdmin ;
	//trace(url);
	var rtn = AxMsie.Navigate2(url, ,"_self", "", "");


}



function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    gfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam); // 공통 키 이벤트 처리
}



