}



function lfn_Form_OnUnloadCompleted(obj) {
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    gfn_DeleteOpenedMenu(ls_FormId[0]);
    gfn_Form_OnUnloadCompleted(obj);
}





