#include "LIB::COMMON.js"; // 공통 관련

var start_time = Time();

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj);
    
    /*
    if (IsExistVar("sMsg")) {
        lbInfomsg.Text = sMsg; // "주민번호를 입력중입니다." 
    }
    */
    //lbInfomsg.Text = "주민번호(전화번호/사업자번호)를 입력중입니다." ; 
    parent.sCloseYnInfo = "";
    SetTimer(5, 100); // Timer Start
    
}

function Form_OnUnloadCompleted(obj) {
    KillTimer(5); // TimerKill      

    gfn_Form_OnUnloadCompleted(obj);
}
/* 공통 필수 종료 */

function lfn_COM0030PProgressBar_OnTimer(obj,nEventID)
{
    lbPassed.text = (Time() - start_time) + " 초 경과";
    
    if( parent.sCloseYnInfo == "Y"){
    
        lbInfomsg.Text = "입력이 완료되었습니다.";
		parent.sCloseYnInfo = "";
		Close();    
    }
    
}

function Form_OnTimer(obj,nEventID)
{
    if( parent.sCloseYnInfo == "Y"){
		parent.sCloseYnInfo = "";
		Close();    
    }
	
}
