/*
 * 기능 : 조회
 */
function lfn_Search(obj) {

    if (edCallNo.Text == "" && edMWNM.Text == "" && edMWHP.Text == "" && edMWTEL.Text == "" && edMWPERSONKEY.Text == "") {        
		gfn_Alert("조회 정보를 입력해 주십시오.");
		edMWHP.SetFocus();
		return false;
    }

    medTotalCnt.Value = "";
    ds_oMinwonList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cti:searchMinwonList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oMinwonList=ds_oMinwonList"	// outDs
		,"CALLDN=" + quote(edCallNo.Text)
          + " MWNM=" + quote(edMWNM.Text)
          + " MWHP=" + quote(edMWHP.Text)
          + " MWTEL=" + quote(edMWTEL.Text)
          + " MWPERSONKEY=" + quote(edMWPERSONKEY.Text)
		,"lfn_AfterSearch"
		,false
	);

}

/* 공통 필수 종료 */

/*
 * 기능 :  조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearch(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    medTotalCnt.Value = ds_oMinwonList.GetCount();
    gfn_SearchMessage(gdList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

/* 이벤트 함수 종료 */


/* 사용자 함수 */
function gdList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

function btnIns_OnClick(obj)
{
    var arrArg = Array(1);
    arrArg[0] = "INS"; 

    Close(arrArg);
	
}

function btnSelect_OnClick(obj) {
    var arrArg = Array(12);
    arrArg[0] = "SEL"; 
    arrArg[1] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWNM"); 
    arrArg[2] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWHP"); 
    arrArg[3] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWTEL");
    arrArg[4] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWMAIL");
    arrArg[5] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWFAX"); 
    arrArg[6] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWPERSONKEY");
    arrArg[7] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWGUBUN"); 
    arrArg[8] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWMGNO"); 
    arrArg[9] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWRECVTEL"); 
    arrArg[10] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWLASTSEQ"); 
    arrArg[11] = ds_oMinwonList.GetColumn(ds_oMinwonList.currow,  "MWREMARK"); 
    

    Close(arrArg);
}

function gdList_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (nChar == 13) { // Enter Key
        btnSelect_OnClick(obj);
    }
} 

function edCallNo_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        lfn_Search();
        edMWHP.SetFocus();
    } 
	
}

