function ds_oMinwonList_OnLoadCompleted(obj,nErrorCode,strErrorMsg,nReason)
{
	ds_oMinwonHistoryList.ClearData();
}

function ds_oMinwonList_OnRowPosChanged(obj,nOldRow,nRow)
{
	var vMWMGNO = ds_oMinwonList.GetColumn(nRow,"MWMGNO") ;
	
    ds_oMinwonHistoryList.ClearData(); // 데이타셋 초기화
    
    if (!gfn_IsNull(vMWMGNO)) {	
	
		tit_clearActionInfo();
		tit_addCsvSearchActionInfo("cti:searchMinwonHistoryList", false);
		// 서버 호출 
		tit_callService(
			""
			,""
			,""	// inDs
			,"ds_oMinwonHistoryList=ds_oMinwonHistoryList"	// outDs
			,"MWMGNO=" + quote(vMWMGNO)
			,"lfn_AfterSearchHistory"
			,false
		);
    }
	
}


function lfn_AfterSearchHistory(nErrorCode, strErrorMsg) {

    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    gfn_SearchMessage(gdListH); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.

}

/*
  수신전화 기록이 없어 민원접수를 하지 못하는 경우 강제로 수신전화번호로 등록 가능하게 
*/
function btnManualIns_OnClick(obj)
{
	
	
	if (!gfn_Confirm("입력한 수신번호로 민원접수를 등록 하시겠습니까?")) {	
		return false;		
	}
	
	var vCallNo = edCallNo.Text ;
	
	if (gfn_isNull(vCallNo)) {
		gfn_Alert("수신번호를 입력해주세요.");	
		edCallNo.SetFocus();    
		return false;		
	}
	
	if (length(vCallNo) < 9) {
		gfn_Alert("수신번호 자릿수를 확인해주세요.");	
		edCallNo.SetFocus();    
		return false;		
	}
	
    var arrArg = Array(2);
    arrArg[0] = "MANUALINS"; 
    arrArg[1] = vCallNo ; 

    Close(arrArg);
    
	
}
