Window.Script:
/*
 * 프로그램명 : frmcti0024PBoardContents.xml
 * 설명 : 게시판형 내용팝업
 * 작성일 :  
 * 작성자 :  
 */

#include "LIB::cti.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    if (IsExistVar("sBoardSeq")) {
        lfn_search(obj); // 게시내용 조회
    }
    obj.Title = sFormTitle;
    
    btnDownloadFile.Enable = true;
    
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

function Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam) {
    switch (nChar) {
        case 27 : // 종료(Esc)
            lfn_End();
            break;
    }
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oBoard.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cti:searchBoardenv", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oBoard=ds_oBoard"	// outDs
		,"BSEQ=" + sBoardSeq // args 
		,"lfn_AfterSearchCommonCode"
		,false
	);

}

/* 공통 필수 종료 */

/* 사용자 함수 */


/* 사용자 이벤트 함수 */
/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchCommonCode(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    edCreateDate.text = ds_oBoard.GetColumn(0, "BREGDATE");
    edWriter.Text = ds_oBoard.GetColumn(0, "BREGSABUN");
    edTitle.text = ds_oBoard.GetColumn(0, "BTITLE");
    taContents.Text = replace(ds_oBoard.GetColumn(0, "BCONTENTS"),"|r|n",chr(13)+chr(10));
    edAttachFile.Text = ds_oBoard.GetColumn(0, "AFSAVENM");
    
    if (length(edAttachFile.Text) < 1) {
        btnDownloadFile.Enable = false;
    }
}

function btnDownloadFile_OnClick(obj) {

    fdAttachFile.Type = "SAVE";
    fdAttachFile.FileName = ds_oBoard.GetColumn(0, "AFSAVENM");

    if (fdAttachFile.open()) {
        var arrRtn = tit_fileRead(
            hfAttachFile
            , ds_oBoard.GetColumn(0, "AFDIRPATH")
            , ds_oBoard.GetColumn(0, "AFREALNM")
            , fdAttachFile.FilePath
            , fdAttachFile.FileName
        );

        gfn_Alert(MSGI00060); //다운로드 완료 메시지
    }

}
