function Form_OnLoadCompleted(obj)
{
	 
	gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 폼 환경설정
    ls_SystemGubun = gs_SystemGubun;
    ls_SystemMenuId = gs_SystemMenuId;
    var ls_FormId = split(sFormUrl, "::");
    ls_FormId = split(ls_FormId[1], ".");
    obj.Title = sFormTitle + "(" + ls_FormId[0] + ")";

    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.

    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.
    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정
        lfn_SetFormAuth(true); // 저장 권한
    } else if (ms_FormAuth == "R") {
        lfn_SetFormAuth(false); // 읽기 권한
    } else {
        gfn_Alert(MSGI00080);
        close();
    }

	var nRow = 0;
	for (var i=2004; i<=parseInt(substr(GetDate(),0,4)); i++) {
		nRow = ds_Year.AddRow();
		ds_Year.SetColumn(nRow, "CD", i);
		ds_Year.SetColumn(nRow, "DATA", i);
	}

	var nowYear = parseInt(substr(GetDate(),0,4));     
    cbxYear.Value = nowYear;    
    var nowMonth = lpad(parseInt(substr(GetDate(),4,2))-1, "0", 2);     
   // var nowMonth = parseInt(substr(GetDate(),4,2))-1;     
    if (nowMonth == "00") {
        nowMonth = "12";
        cbxYear.Value = nowYear-1;
    }    
    cbxMonth.Value =nowMonth;     
    cbxYearMon = cbxYear.Value; 
    cbxYearMon = cbxYearMon + cbxMonth.Value;
    cbxYearMon = parseInt(cbxYearMon);
    gfn_SearchJibu(cbxJibu, "Y", "선택"); // 지부 코드 조회
    cbxYear_OnChanged();
    cbxMonth_OnChanged();         
    lbText.Text = "(" + cbxJibu.Text + ")";
    
    gdList4.SetCellProp("head",3,"text",toNumber(substr(GetDate(),0,4))-3+"년");
    gdList4.SetCellProp("head",4,"text",toNumber(substr(GetDate(),0,4))-2+"년");
    gdList4.SetCellProp("head",5,"text",toNumber(substr(GetDate(),0,4))-1+"년");
    gdList4.SetCellProp("head",7,"text",substr(GetDate(),0,4)+"년");
    gdList4.SetCellProp("head",9,"text",toNumber(substr(GetDate(),0,4))+1+"년 이후");
    
}

