//년도
function cbxYear_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	stYear.Text = cbxYear.Text;
    cbxYearMon = cbxYear.Value; 
    cbxYearMon = cbxYearMon + cbxMonth.Value;
    cbxYearMon = parseInt(cbxYearMon);
	
	if (cbxYearMon < 200912) {
	    gdList5.Visible  = true;
	    gdList51.Visible = false;
	} else {
	    gdList5.Visible  = false;
	    gdList51.Visible = true;	
	}     
}

//월
function cbxMonth_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	var strDate = GetDate();	
	stMonth.Text = cbxMonth.Text;
	var StartYear = parseInt(cbxYear.Value); // 현재년도
    StartYear1 = StartYear+".01.01";
    var lastYear = "";
    if (cbxMonth.Text == "01") {
		lastYear = parseInt(cbxYear.Value) - 1; // 1월일 경우 전년
    } else {
		lastYear = cbxYear.Value;
   }
    lastYear1 = lastYear+".01.01";
    
    StartYear2 = StartYear + cbxMonth.Value+"01";
    StartYear2 = addDate(StartYear2,-1);
    StartYear2 = substr(StartYear2,4,2) + "." + substr(StartYear2,6,2);

    var nowMonth = parseInt(substr(GetDate(),0,6)); 
    nowMonth = nowMonth+'01';
    nowMonth = substr(nowMonth,0,4) + "." + substr(nowMonth,4,2) + "." + substr(nowMonth,6,2);
    
    var lastday = "";
    if ((cbxMonth.Value == "01") || (cbxMonth.Value == "03") || (cbxMonth.Value == "05") || 
       (cbxMonth.Value == "07") || (cbxMonth.Value == "08") || (cbxMonth.Value == "10") || 
       (cbxMonth.Value == "12")) {lastday = "31";}
    else if ((cbxMonth.Value == "04") || (cbxMonth.Value == "06") || (cbxMonth.Value == "09") || 
       (cbxMonth.Value == "11")) {lastday = "30";}
    else if (cbxMonth.Value == "02") {lastday = "28";}
    nowDay = cbxMonth.Value + "." + lastday;
/*    nowDay = substr(nowDay,0,4)+"0101";
    nowDay = addDate(nowDay,-1);
    nowDay = substr(nowDay,4,2) + "." + substr(nowDay,6,2);*/
    
    var Nm = "전 월\n"+"("+lastYear1+"~"+StartYear2+")";
    gdList2.SetCellProp("head",2,"text",Nm);
    
    Name2 = "기준월\n"+"("+StartYear1+"~"+nowDay+")";
    gdList2.SetCellProp("head",3,"text",Name2); 
        
    var Nm = "전 월\n"+"("+lastYear1+"~"+StartYear2+")";
    gdList6.SetCellProp("head",1,"text",Nm);
    
    Name2 = "기준월\n"+"("+StartYear1+"~"+nowDay+")";
    gdList6.SetCellProp("head",2,"text",Name2); 
        
    btnSearch_OnClick();
}



function btnSearch_OnClick(obj)
{
    Search_List1();
    if (returnval == 0) {
        return false; 
    }    
    Search_List2();
    if (returnval == 0) {
        return false; 
    }  
	Search_List3();
    if (returnval == 0) {
        return false; 
    }     
    Search_List4();
    if (returnval == 0) {
        return false; 
    }
    cbxYearMon = cbxYear.Value; 
    cbxYearMon = cbxYearMon + cbxMonth.Value;
    cbxYearMon = parseInt(cbxYearMon);
    //2009년 회비수납현황과 2010년 회비수납현황의 쿼리가 클리다.
	if (cbxYearMon < 200912) {
	    gdList5.Visible  = true;
	    gdList51.Visible = false;
	    Search_List5();
	} else {
	    gdList5.Visible  = false;
	    gdList51.Visible = true;	
	    Search_List51();
	}  
    if (returnval == 0) {
        return false; 
    }       
    Search_List6();
    if (returnval == 0) {
        return false; 
    }        
    Search_List7();
    if (returnval == 0) {
        return false; 
    }
       
    lbText.Text = "(" + cbxJibu.Text + ")";
    
}


/*--------------------------------------
 * 1. 회비/실무교육비 현황
 *--------------------------------------*/
function Search_List1(obj)
{
    ds_Data1.Clear();
	tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchGroup0210_1",false);	
	
	var yearMonth = cbxYear.Value + "" + cbxMonth.Value; //200911
	var yearMonth = parseInt(yearMonth)+1;
	var befoMonth = cbxYear.Value + "" + ToString(cbxMonth.Value); //200910
	if (cbxMonth.Value == "01") {
	    befoMonth = ToString(parseInt(cbxYear.Value)-1) +""+ "12"; 
	}
	if (cbxMonth.Value == "12") {
	    yearMonth = ToString(parseInt(cbxYear.Value)+1) +""+ "01"; 	    
	}	
	
	var sParm = "";
	sParm  = " JIBU="        + quote(cbxJibu.Value); 
	sParm += " TODAYMONTH="  + quote(yearMonth); 
	sParm += " BEFOREMONTH=" + quote(befoMonth); 
	sParm += " YEAR06=" + quote(ToString(parseInt(cbxYear.Value)-3)); 
	sParm += " YEAR07=" + quote(ToString(parseInt(cbxYear.Value)-2)); 
	sParm += " YEAR08=" + quote(ToString(parseInt(cbxYear.Value)-1)); 
	sParm += " YEAR09=" + quote(ToString(parseInt(cbxYear.Value))); 
	sParm += " YEAR10=" + quote(ToString(parseInt(cbxYear.Value)+1)); 	  
	          	
	tit_callService(
	                ""
	               , ""  
	               , ""//inDs 
	               , "ds_Data1=ds_oD1 " //outDs  
	               , sParm
	               , "afterSearch"  //callBack
	               , false
	               , false
	               , true
	               ); 

}

