// 조회 후 callbackfunction afterSearch(errCode, errMsg){

	if(errCode != 0 ){
		//alert("오류가 발생했습니다.");
        //gfn_ErrorMsg(nErrorCode, strErrorMsg);
        //alert(errCode);
        //alert(errMsg);
        returnval = 0;
        return false;		
	}
	returnval = 1;
}



