#include "LIB::cust.js"; // 공통업무 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    
    // 콜센터 직원 사용 이면
	if (gs_IsCallCenter == "Y") {
		btnToExcel.Enable = false; // 엑셀출력 버튼
	}
      
    edCount.Text = 300;
    // 공통 코드 조회
    gfn_SearchAllJibu(cbxJibu, "Y", "전지부"); 											// 지부 코드 조회
    gfn_SearchCommonCode(ds_oBizGubun, "BIZCD", "Y", "");
    gfn_SearchCustomerCourseFullName(ds_oCourse, "0", "전체");							// 대표고객직능 조회
    cbxCourse.index = 0;
    
    cbxJibu.Value = sGtmgno;	// 지부 설정

    if (IsExistVar("sGubun")) {   // sKeyword , sGubun 인자값
        radAgent.Value = sGubun;
        edKeyWord.Text = sKeyword;
        edRoadKeyWord.Text = sRoadKeyword;
        
        radAgent_OnChanged(obj,radAgent.Value);
        sGubun = "";
        lfn_Search();
    } else {
		radAgent.Value = 15;
		edKeyWord.SetFocus();
    }
    
    if (radAgent.Value == "3" || radAgent.Value == "12" || radAgent.Value == "14" || 
        radAgent.Value == "15" || radAgent.Value == "17") {
		chkCust.Enable = false;
		chkCust.Value = 1;
    } else {
		chkCust.Enable = true;
		chkCust.Value = 0;    
    }
    cbxYear.Value = 0; // 24.02 서울지부 요청 정상자료로 셋팅 

}

function Form_OnUnloadCompleted(obj) {
    if (gs_IsCallCenter == "Y") { // 콜센터 직원용 이면
		gs_SystemGubun = "crs";
	} else if (gs_IsTlogin == "Y") { // 외부 출장용 이면
		gs_SystemGubun = "outerbiz";
	} else {
		gs_SystemGubun = "cust";
	}
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    if (gs_IsCallCenter == "Y") { // 콜센터 직원용 이면
		gs_SystemGubun = "crs";
	} else if (gs_IsTlogin == "Y") { // 외부 출장용 이면
		gs_SystemGubun = "outerbiz";
	} else {
		gs_SystemGubun = "cust";
	}
    Close();
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    
    var sKeyWord = edKeyWord.Text;
    var sRoadKeyWord = edRoadKeyWord.Text;
    var sBirthday = "";
    var sDI = "";
    
    if (!gfn_FormValidate(this)) { // 입력 Validation Check
        return false;
    }
    if (radAgent.Value == "4") {
        if (gfn_IsNull(edAddr1.Text) || gfn_IsNull(edAddr2.Text)) {
            gfn_Alert("주소나 번지중 최소 하나는 입력하십시오.");
            edAddr1.SetFocus();
            return false;
        }
    }

    if (radAgent.Value == "1") {
        sKeyWord = lfn_ReplaceBnm(sKeyWord);  // 검색대상물명
    }
    
    
    
    ds_oGroupList.ClearData(); // 데이타셋 초기화
    
  

    // 조회이력을 저장한다.

	ls_HistoryArg = "지부=" + cbxJibu.Text;
	ls_HistoryArg += ", 직능=" + cbxCourse.Text;

    if (!gfn_IsNull(sKeyWord)) {
		// 주민번호 검색인 경우
		if (radAgent.Value == 6) {	// 선임자주민번호이면 7자리만 이력남김
			ls_HistoryArg += ", " + radAgent.Text + "=" + substr(sKeyWord,0,7);
			if (length(sKeyWord) <> 13) {
				sBirthday = sKeyWord;
			} else {
				Https.Sync = true;
				sDI = gfn_SearchSCI("1", sKeyWord);	// DI정보 가져오기
				Https.Sync = false;
			}
		} else {
			ls_HistoryArg += ", " + radAgent.Text + "=" + sKeyWord;
		}
	} else if (!gfn_IsNull(sRoadKeyWord)) {
		ls_HistoryArg += ", " + radAgent.Text + "=" + sRoadKeyWord;
	} else if (!gfn_IsNull(edAddr1.Text) || !gfn_IsNull(edAddr2.Text)) {
		ls_HistoryArg += ", " + radAgent.Text + "=" + edAddr1.Text+ " "+edAddr2.Text;
	}
           
    gfn_InsertSearchPrintHistory(
        "S"
        , this.ID
        , global.mnuLeft.cbxOpenForm.Text
        , ls_HistoryArg
    );
    
	// 콜센터 직원용 이면
	if (gs_IsCallCenter == "Y") {
		// 콜센터 직원 조회이력 저장
		gfn_InsertCallCenterSearchPrintHistory(
				"S"
				, gs_CallCenterUID				, gds_OpenedForm.GetColumn(gds_OpenedForm.FindRow("MURL",this.ID),"MMENUID")
				, this.ID				, global.mnuLeft.cbxOpenForm.Text				, ls_HistoryArg
			);
	}


    tit_clearActionInfo();
	tit_addCsvSearchActionInfo("cust:searchCust0001GroupList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oGroupList=ds_oGroupList"	// outDs
		,"GUBUN=" + quote(radAgent.Value)       
            + " KEYWORD=" + quote(sKeyWord)
            + " ROADKEYWORD=" + quote(sRoadKeyWord)
            + " ADDR1=" + quote(edAddr1.Text)
            + " ADDR2=" + quote(edAddr2.Text)
            + " ROWNUM=" + quote(edCount.Text)
            + " CDELGUBUN=" + quote(cbxYear.Value)
            + " CGTMGNO=" + quote(cbxJibu.Value) // quote(sGtmgno)
            + " CHECK=" + quote(chkCust.Value)
            + " COURSECD=" + quote(cbxCourse.Value)
            + " BIRTHDAY=" + quote(sBirthday)
            + " DI=" + quote(sDI)
            
		,"lfn_AfterSearchGroup"
		,false
	);

}

/* 공통 필수 종료 */

/*
 * 기능 : 공통코드 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchGroup(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    //trace(ds_oGroupList.SaveXML());
    if (radAgent.Value == 8) {
        gdGroupList.Format = "Default1";
    } else if (radAgent.Value == 9) {
        gdGroupList.Format = "Default2";
    } else if (radAgent.Value == 10) {
        gdGroupList.Format = "Default3";
    } else {
        gdGroupList.Format = "Default";
    }
   
    if (ds_oGroupList.GetCount() == 1) {
        var arrArg = Array(2);
        arrArg[0] = ds_oGroupList.GetColumn(ds_oGroupList.currow,  "CGTMGNO");		        //고객정보관리번호
        arrArg[1] = ds_oGroupList.GetColumn(ds_oGroupList.currow,  "CNO");		    //지부        
        
        //Close(arrArg);
    }

    gfn_SearchMessage(gdGroupList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}

