function btnSelect_OnClick(obj)
{
    if (gs_IsCallCenter == "Y") { // 콜센터 직원용 이면
		gs_SystemGubun = "crs";
	} else if (gs_IsTlogin == "Y") { // 외부 출장용 이면
		gs_SystemGubun = "outerbiz";
	} else {
		gs_SystemGubun = "cust";
	}
    var arrArg = Array(2);
    arrArg[0] = ds_oGroupList.GetColumn(ds_oGroupList.currow,  "CGTMGNO");		        //고객정보관리번호
    arrArg[1] = ds_oGroupList.GetColumn(ds_oGroupList.currow,  "CNO");		    //지부

    Close(arrArg);
}

function gdGroupList_OnCellDblClick(obj,nRow,nCell,nX,nY,nPivotIndex) {
    btnSelect_OnClick(obj);
}

function edKeyWord_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) { // Enter Key
        lfn_Search();
    }
}
