function radAgent_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{

	if (strCode == "4") {	// 주소(동)+번지
		chkCust.Enable = true;
		chkCust.Value = 0;
        lbAddr1.Visible = true;
        edAddr1.Visible = true;
        lbBunji.Visible = true;
        edAddr2.Visible = true;
        lbKeyword.Visible = false;
        edKeyWord.Visible = false;
        edKeyWord.Text = "";
        lbRoadAddr1.Visible = false;
        edRoadKeyWord.Visible = false;
        edRoadKeyWord.Text = "";
        if (!IsExistVar("sGubun")) edAddr1.Text = "";
        edAddr1.SetFocus();
	} else if (strCode == "15") {	// 대상물명or선임자명or주소(동)or번지
		chkCust.Enable = false;
		chkCust.Value = 1;
        lbAddr1.Visible = false;
        edAddr1.Visible = false;
        lbBunji.Visible = false;
        edAddr2.Visible = false;
        lbRoadAddr1.Visible = false;
        edRoadKeyWord.Visible = false;
        edRoadKeyWord.Text = "";
        lbKeyword.Visible = true;
        edKeyWord.Visible = true;
       	if (!IsExistVar("sGubun")) edKeyWord.Text = "";
        edKeyWord.SetFocus();
	} else if (strCode == "16" || strCode == "17" || strCode == "18" || strCode == "19") {	// 대상물명or선임자명or도로명or건물번호
		chkCust.Enable = false;
		chkCust.Value = 1;
        lbAddr1.Visible = false;
        edAddr1.Visible = false;
        lbBunji.Visible = false;
        edAddr2.Visible = false;
        lbRoadAddr1.Visible = true;
        edKeyWord.Visible = false;
        edKeyWord.Text = "";
        edRoadKeyWord.Visible = true;
       	if (!IsExistVar("sGubun")) edRoadKeyWord.Text = "";
        edRoadKeyWord.SetFocus();
	} else if (strCode == "6") {	// 선임자 주민번호이면
        chkCust.Enable = false;		// 해당문자열포함 선택불가
        chkCust.Value = 0;			// 해당문자열포함 해제
        edKeyWord.SetFocus();
	} else {
		if (strCode == "3"||strCode == "12"||strCode == "14") {	// 번지
			chkCust.Enable = false;
			chkCust.Value = 1;
		} else {
			chkCust.Enable = true;
			chkCust.Value = 0;
		}
        lbAddr1.Visible = false;
        edAddr1.Visible = false;
        lbBunji.Visible = false;
        edAddr2.Visible = false;
        lbRoadAddr1.Visible = false;
        edRoadKeyWord.Visible = false;
        edRoadKeyWord.Text = "";
        lbKeyword.Visible = true;
        edKeyWord.Visible = true;
        if (!IsExistVar("sGubun")) edKeyWord.Text = "";
        edKeyWord.SetFocus();
	}
}

function gdGroupList_OnKeyDown(obj,nChar,bShift,bCtrl,bAlt,nLLParam,nHLParam)
{
	if (nChar == 13) { // Enter Key
        btnSelect_OnClick();
    }
}

function lfn_Form_OnKeyDown(obj,objSenderObj,nChar,bShift,bControl,bAlt,nLLParam,nHLParam)
{
	if (bAlt) {
        ms_BarHotKeyCount++;
        if (ms_BarHotKeyCount == 2) {
            ms_BarHotKeyCount = 0;
            medBarCode.SetFocus();
        }
    } else {
        ms_BarHotKeyCount = 0;

        switch (nChar) {
            case 117 : // 조회(F2)
                if (obj.btnSearch.Enable) {
                    obj.lfn_Search();
                }
                break;
            case 122 : // 엑셀(F11)
                if (obj.btnToExcel.Enable) {
                    obj.lfn_excel();
                }
                break;
            case 27 : // 종료(Esc)
                if (obj.btnEnd.Enable) {
                    if (gs_IsCallCenter == "Y") { // 콜센터 직원용 이면
						gs_SystemGubun = "crs";
					} else if (gs_IsTlogin == "Y") { // 외부 출장용 이면
						gs_SystemGubun = "outerbiz";
					} else {
						gs_SystemGubun = "cust";
					}
                    Close();
                }
                break;
        }
    }
}

/*
 * 기능 : 검색을 위한 대상물명 변환
 * parameter : 대상물명
 * return : 대상물명(BSEARCHNM)
 */
function lfn_ReplaceBnm(sBnm) {
    var sBSEARCHNM = sBnm;
    ds_oBnmCode.ClearData();
    
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010BnmReplaceCode", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oBnmCode=ds_oBnmCode" // outDs
        , ""
        , ""
        ,false
        ,false
        ,true
    );
    
    for (i=0;i<ds_oBnmCode.GetCount();i++) {
        sBSEARCHNM = Replace(sBSEARCHNM, ds_oBnmCode.GetColumn(i, "CCCDNM"), "");
    }
    
    return sBSEARCHNM;
}

function Form_OnActivate(obj)
{
	gs_SysID = "[stm_01,bsn_01,dsc_0004]";	// 개인정보접속기록 시스템ID  
}
