#include "LIB::cust.js"; // 공통 관련

/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리
    gfn_MakeRequiredInputMark(this); // 필수 입력란을 설정한다.
 
    ms_FormAuth = gfn_GetFormAuth(this); // 권한을 조회한다.    if (ms_FormAuth == "W") { // 권한에 따른 버튼 설정        lfn_SetFormAuth(true); // 저장 권한    } else if (ms_FormAuth == "R") {        lfn_SetFormAuth(false); // 읽기 권한    } else {        gfn_Alert(MSGI00080);        close();    }
    
    gfn_SearchCommonCode(ds_oBizCd, "BIZCD", "Y", "선택사항"); 				// 방관-용도코드    
    cbxBusiness.Value = "";
    edBuildingNm.SetFocus();
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}

/* * 기능 : 버튼 활성/비활성시킨다. * parameter : bStatus - 활성/비활성여부(true:활성, false:비활성) * return : 없음 */
function lfn_SetFormAuth(bStatus) {	btnSave.Enable = bStatus; // 수정&저장 버튼}

// 대상물 관리번호
function lfn_SearchCust0010BuildingSeq(obj) {
	
	ds_oBuildingSeq.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
    tit_addSearchActionInfo("cust:searchCust0010BuildingSeq", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oBuildingSeq=ds_oBuildingSeq" // outDs
        , ""
        , ""
        ,false
        ,false
        ,true
    );
}

/*
 * 기능 : 저장
 */
function lfn_Save(obj) {
    if( gfn_IsNull(edBuildingNm.Text) ) {
		//회비
		gfn_Alert(lbBuildingNm.Text + MSGW00010);	//대상물명을(를) 입력해 주십시오.
		edBuildingNm.SetFocus();
		return;
	}
	
	// 대상물 주소
	if (radAddrGubun.Value == "0") {
		if(	gfn_IsNull(edOldAddr1.Text) || gfn_IsNull(edOldAddr2.Text) || gfn_IsNull(medOldZipCode.Value) ) {
			//회비
			gfn_Alert("대상물 대표주소는 필수입력항목 입니다.");
			edOldAddr1.SetFocus();
			return;
		}
    } else {
		if(	gfn_IsNull(edNewAddr1.Text) || gfn_IsNull(edNewAddr2.Text) || gfn_IsNull(medNewZipCode.Value) ) {
			//회비
			gfn_Alert("대상물 대표주소는 필수입력항목 입니다.");
			edOldAddr1.SetFocus();
			return;
		}
    
    }
    
    for(var i=0; i<ds_ioOwnerInfo.GetRowCount(); i++) {	//관계자 필수입력 체크(성명)
        if( ds_ioOwnerInfo.GetRowType(i) <> "normal" ) {	//수정되었고
            if( gfn_IsNull(ds_ioOwnerInfo.GetColumn(i, "ONM")) ) {
                gfn_Alert("[관계자정보] 성명은(는) 필수입력항목 입니다.");
                div6.tabTab1.tabpgTab1Page1.gdConnections.SetFocus();
                return;
            }
        }
    }
    for(var i=0; i<ds_ioDangerInfo.GetRowCount(); i++) {	//위험물 필수입력 체크(허가번호)
        if( ds_ioDangerInfo.GetRowType(i) <> "normal" ) {	//수정되었고
            if( gfn_IsNull(ds_ioDangerInfo.GetColumn(i, "DPERMISSIONNO")) ) {
                gfn_Alert("[위험물부가정보] 허가번호은(는) 필수입력항목 입니다.");
                div6.tabTab1.tabpgTab1Page2.gdDangerInfo.SetFocus();
                return;
            }
        }
    }
    for(var i=0; i<ds_ioTankInfo.GetRowCount(); i++) {	//이동탱크 필수입력 체크(차량번호)
        if( ds_ioTankInfo.GetRowType(i) <> "normal" ) {	//수정되었고
            if( gfn_IsNull(ds_ioTankInfo.GetColumn(i, "TCARNO")) ) {
                gfn_Alert("[이동탱크부가정보] 차량번호은(는) 필수입력항목 입니다.");
                Div6_tabTab1_tabpgTab1Page3.ds_ioTankInfo.SetFocus();
                return;
            }
        }
    }
    
    if (!gfn_Confirm("정말로 저장 하시겠습니까?")) { // 정말로 추가 저장 하시겠습니까?
        return false;
    }
    
    var sBSEARCHNM = lfn_ReplaceBnm(edBuildingNm.Text);  // 검색대상물명
    
    ds_ioBuildingInfo.InsertRow(0);
    ds_ioBuildingInfo.SetColumn(0, "BNM", edBuildingNm.Text);    
    ds_ioBuildingInfo.SetColumn(0, "BADDR1", edOldAddr1.Text);
    ds_ioBuildingInfo.SetColumn(0, "BADDR2", edOldAddr2.Text);
    ds_ioBuildingInfo.SetColumn(0, "BZIPCD", medOldZipCode.Value);
    ds_ioBuildingInfo.SetColumn(0, "BROADADDR1", edNewAddr1.Text);
    ds_ioBuildingInfo.SetColumn(0, "BROADADDR2", edNewAddr2.Text);
    ds_ioBuildingInfo.SetColumn(0, "BZIPCD", medNewZipCode.Value);
    ds_ioBuildingInfo.SetColumn(0, "BADDRGUBUN", radAddrGubun.Value);
    ds_ioBuildingInfo.SetColumn(0, "BTEL", edTelNo.Text);
    ds_ioBuildingInfo.SetColumn(0, "BFAX", edFaxNo.Text);
    ds_ioBuildingInfo.SetColumn(0, "BBIZCD", cbxBusiness.Value);
    ds_ioBuildingInfo.SetColumn(0, "BSMOKEGUBUN", cbxEquipmentYn.Value);
    ds_ioBuildingInfo.SetColumn(0, "BAREA", medArea.Value);
    ds_ioBuildingInfo.SetColumn(0, "BCONO", medCompanyNo.Value);
    ds_ioBuildingInfo.SetColumn(0, "BSEARCHNM", sBSEARCHNM);
    
    var iDsList = "";	//input Dataset List
    var iArgsNm = "";
    
    var iBuildingDsNm = "ds_ioBuildingInfo=ds_ioBuildingInfo:U";
    var iOwnerDsNm  = " ds_ioOwnerInfo=ds_ioOwnerInfo:U";
    var iDangerDsNm = " ds_ioDangerInfo=ds_ioDangerInfo:U";
	var iTankDsNm   = " ds_ioTankInfo=ds_ioTankInfo:U";
	
	lfn_SearchCust0010BuildingSeq();        // 대상물관리번호
	iArgsNm += "SBMGNO="  + quote(ds_oBuildingSeq.GetColumn(0, "SEQ"));
	
	tit_clearActionInfo();
    tit_addSingleActionInfo("cust:insertCust0010BuildingInfo", "", 0, "");
    iDsList += iBuildingDsNm;    
    if (ds_ioOwnerInfo.GetUpdate() == true) {
		tit_addMultiActionInfo("cust:updateCust0010OwnerInfo", "", 0, "", "N");		//관계자
		iDsList += iOwnerDsNm;
	}
	if (ds_ioDangerInfo.GetUpdate() == true) {
		tit_addMultiActionInfo("cust:updateCust0010DangerInfo", "", 0, "", "N");	//위험물
		iDsList += iDangerDsNm;
	}
	if (ds_ioTankInfo.GetUpdate() == true) {
		tit_addMultiActionInfo("cust:updateCust0010TankInfo", "", 0, "", "N");		//이동탱크
		iDsList += iTankDsNm;
	}
	
	// 서버 호출 
	tit_callService(
		""
		,""
		,iDsList	// inDs
		,""	// outDs
		,iArgsNm	// args
		,"lfn_AfterSaveCaseCombination"
		,false
	);
}

/*
 * 기능 : 저장 후 처리해야 할 내용
 * parameter : ErrorCode - 에러코드
 *             ErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSaveCaseCombination(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }
    
    gfn_Alert("정상적으로 저장되었습니다.");
    var arrArg = Array(1);
    arrArg[0] = ds_oBuildingSeq.GetColumn(0, "SEQ");    // 대상물관리번호 
    
    Close(arrArg);
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}
/* 공통 필수 종료 */

