/* 공통 이벤트 함수 */
function btnSearchOldZipCode_OnClick(obj)
{
/*
	var arrZipCode = gfn_ZipCodeListPopup(edOldAddr1.Text); // 우편번호 조회 팝업
    if (length(arrZipCode) > 0) {
        medOldZipCode.Value = arrZipCode[0];
        edOldAddr1.Text = arrZipCode[1];
        edOldAddr2.SetFocus();
    }
*/
    
	var sAddrGubun = 0;
	
	if (radAddrGubun.Index == 0) {	// 구주소
		sAddrGubun = 0;
	} else {
		sAddrGubun = 1;
	}
	
	if ( radAddrGubun.Value == 0)
	{
		// 지번주소/도로명주소 조회 팝업
		var arrAddrInfo = gfn_PostRoadListPopup(gds_oUserInfo.GetColumn(0, "PDEPTCD")		// 지부코드
												, sAddrGubun		// 구새주소 구분[0:구주소, 1:새주소]
												, medOldZipCode.Value			// 구:지번 우편번호
												, edOldAddr1.Text		// 구:지번주소1
												, edOldAddr2.Text		// 구:지번주소2
												, ""		// 구:산여부
												, ""	// 구:주번지
												, ""		// 구:부번지
												, ""		// 구:아파트,건물명
												, ""	// 구:상세주소
												, medNewZipCode.Value	// 새1:도로명 우편번호
												, edNewAddr1.Text		// 새1:도로명주소11
												, edNewAddr2.Text		// 새1:도로명주소12
												, ""	// 새1:건물관리번호1
												, ""	// 새1:상세주소1
												, ""		// 새1:부가주소1
												, medNewZipCode.Value	// 새2:도로명 우편번호
												, edNewAddr1.Text		// 새2:도로명주소21
												, edNewAddr2.Text		// 새2:도로명주소22
												, ""	// 새2:건물관리번호2
												, ""	// 새2:상세주소2
												, ""	// 새2:부가주소2
												, ""
												); 	
		if (length(arrAddrInfo) > 0) {	
			if (arrAddrInfo[0] == 0) {
				
				// 지번주소 조회 Tab 
				// 구주소
				medOldZipCode.Value = arrAddrInfo[1];
		
				edOldAddr1.Text = arrAddrInfo[2];		
				edOldAddr2.Text = arrAddrInfo[3];
				
				// 새주소
				
				if (!gfn_IsNull(arrAddrInfo[10])) {
				
					medNewZipCode.Value = arrAddrInfo[10];
					
				}
				
				edNewAddr1.Text = arrAddrInfo[11];
				
				edNewAddr2.Text = arrAddrInfo[12];		
				ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);		// PNU 코드
				
				ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				
				ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				
				ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소
					   
				ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);
				
				ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);
				
				ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);
				
				ds_ioBuildingInfo.SetColumn(0, "BRBUILDNM", arrAddrInfo[7]);
				
				ds_ioBuildingInfo.SetColumn(0, "BRADDRETC", arrAddrInfo[8]);
	
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM
					
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS
				
				} else {
				
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM
					
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS
					
				}
				
	
			} else {
			
				// 도로명주소 조회 Tab
				medNewZipCode.Value = arrAddrInfo[10];	// 우편번호
				edNewAddr1.Text = arrAddrInfo[11];		// 도로명주소1
				edNewAddr2.Text = arrAddrInfo[12];		// 도로명주소2
				ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", arrAddrInfo[4]);		// 산여부
				ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", arrAddrInfo[5]);		// 주번지
				ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", arrAddrInfo[6]);		// 부번지
				ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", arrAddrInfo[9]);			// PNU 코드
				ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", arrAddrInfo[13]);	// 건물관리번호
				ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", arrAddrInfo[14]);		// 상세주소
				ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", arrAddrInfo[15]);		// 부가주소
				
				if (!gfn_IsNull(arrAddrInfo[11])) {	// 도로명주소1
				
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");	// BRCONVPGM
					
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");	// FBRCONVSTATUS
				
				} else {
				
					ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "0");	// BRCONVPGM
					
					ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "00");	// BRCONVSTATUS
					
				}
				
				radAddrGubun.Value = 1;	// 도로명주소
				
			}
	
		}
	}
	else 
	{
		var arrAddrInfo = gfn_PostRoadListPopup_New();
		if (length(arrAddrInfo) > 0) {
			radAddrGubun.Value = 1;	// 도로명주소
			ds_ioBuildingInfo.SetColumn(0, "BADDRGUBUN", "1");
			medNewZipCode.Value = arrAddrInfo[5];									// 우편번호
			edNewAddr1.Text = arrAddrInfo[1];										// 도로명주소
			edNewAddr2.Text = arrAddrInfo[29];										// 지번주소
			ds_ioBuildingInfo.SetColumn(0, "BRSANGUBUN", 		arrAddrInfo[15]);	// 산여부(0:대지, 1:산)
			ds_ioBuildingInfo.SetColumn(0, "BRMAINBUNJI", 		arrAddrInfo[22]);	// 주번지(지번주번지)
			ds_ioBuildingInfo.SetColumn(0, "BRSUBBUNJI", 		arrAddrInfo[23]);	// 부번지(지번부번지, 부번지없을 경우 0)
			ds_ioBuildingInfo.SetColumn(0, "BRPNUCD", "");							// PNU 코드 : 공백
			ds_ioBuildingInfo.SetColumn(0, "BRROADBUILDMGNO", 	arrAddrInfo[8]);	// 건물관리번호
			ds_ioBuildingInfo.SetColumn(0, "BRROADDETAIL", 		arrAddrInfo[1]);	// 상세주소(도로명주소)
			ds_ioBuildingInfo.SetColumn(0, "BRROADREMAIN", "");						// 부가주소
			ds_ioBuildingInfo.SetColumn(0, "BRCONVPGM", "1");
			ds_ioBuildingInfo.SetColumn(0, "BRCONVSTATUS", "01");
		}
	}	
}
    

// 대상물 주소1
function edOldAddr1_OnKeyDown(obj,nChar,bShift,bCtrl, bAlt,LLParam,HLParam)
{
	if (nChar == 13) {
        btnSearchOldZipCode_OnClick();
	} else {
		gfn_EnterNextFocus(obj);
	}
}

//사업자번호 체크
function medCompanyNo_OnKillFocus(obj)
{
	var bCheck = lfn_CompanyNoCheck(trim(medCompanyNo.Value));
	
	if(bCheck == false) {	//실패
		if (!gfn_Confirm("사업자등록번호가 형식에 맞지않습니다.\n강제로 저장 하시겠습니까?")) {
            medCompanyNo.SetFocus();
            return false;
        }
	}
}

// 행추가 (대상물정보 2종셋트 - 위험물부가정보)
function Div6_btnAddRow1_OnClick(obj)
{
	switch(Div6.tabTab1.TabIndex)
	{
		case 0 :	//관계자 정보
			if(ds_ioOwnerInfo.rowcount > 0) {	//데이타 존재
				if(ds_ioOwnerInfo.GetRowType(ds_ioOwnerInfo.lastrow) == "insert") {	//마지막 행이 insert 상태
					if( gfn_IsNull(ds_ioOwnerInfo.GetColumn(ds_ioOwnerInfo.lastrow, "OGUBUN"))
						|| gfn_IsNull(ds_ioOwnerInfo.GetColumn(ds_ioOwnerInfo.lastrow, "ONM")) ) {
						gfn_Alert("이전 행 정보의 일부가 입력되지 않아 행추가는 취소되었습니다.");
						return;
					}
				}
			}
			var nRow = ds_ioOwnerInfo.AddRow();
			ds_ioOwnerInfo.SetColumn(nRow, "OGUBUN", ds_oOwnerGubun.GetColumn(0,"CD"));
			Div6.tabTab1.tabpgTab1Page1.gdConnections.SetFocus();
			Div6.tabTab1.tabpgTab1Page1.gdConnections.SetCellPos(Div6.tabTab1.tabpgTab1Page1.gdConnections.GetCellCount("Head")-4);
		break;
		case 1 :	//위험물 부가정보
			if(ds_ioDangerInfo.rowcount > 0) {	//데이타 존재
				if(ds_ioDangerInfo.GetRowType(ds_ioDangerInfo.lastrow) == "insert") {	//마지막 행이 insert 상태
					if( gfn_IsNull(ds_ioDangerInfo.GetColumn(ds_ioDangerInfo.lastrow, "DPERMISSIONNO")) ) {
						gfn_Alert("이전 행 정보의 일부가 입력되지 않아 행추가는 취소되었습니다.");
						return;
					}
				}
			}
			var nRow = ds_ioDangerInfo.AddRow();
			ds_ioDangerInfo.SetColumn(nRow, "OGUBUN", ds_ioDangerInfo.GetColumn(0,"CD"));
			Div6.tabTab1.tabpgTab1Page2.gdDangerInfo.SetFocus();
			Div6.tabTab1.tabpgTab1Page2.gdDangerInfo.SetCellPos(Div6.tabTab1.tabpgTab1Page2.gdDangerInfo.GetCellCount("Head")-2);
		break;
		case 2 :	//이동탱크 부가정보
            Dialog("cust::frmcust0010PTankInfo.xml", "", 500, 308, "", -1, -1);			
		break;
	}
}

// 행삭제 (대상물정보 2종셋트 - 위험물부가정보)
function Div6_btnDelRow1_OnClick(obj)
{
	switch(Div6.tabTab1.TabIndex)
	{
		case 0 :
			if(gfn_IsNull(ds_ioOwnerInfo.GetColumn(ds_ioOwnerInfo.currow, "OBMGNO"))) {
				ds_ioOwnerInfo.DeleteRow(ds_ioOwnerInfo.currow);
				return;
			}
		break;
		case 1 :
			if(gfn_IsNull(ds_ioDangerInfo.GetColumn(ds_ioDangerInfo.currow, "DBMGNO"))) {
				ds_ioDangerInfo.DeleteRow(ds_ioDangerInfo.currow);
				return;
			}
		break;
		case 2 :
			if(gfn_IsNull(ds_ioTankInfo.GetColumn(ds_ioTankInfo.currow, "TBMGNO"))) {
				ds_ioTankInfo.DeleteRow(ds_ioTankInfo.currow);
				return;
			}
		break;
	}
}

//이동탱크부가정보 상세보기 팝업
function Div6_tabTab1_tabpgTab1Page3_gdTankInfo_OnCellClick(obj,nRow,nCell,nX,nY,nPivotIndex)
{
	if(nCell == 4) {
		//이동탱크 부가정보
		var args  = "sRow="+quote(nRow);
		Dialog("cust::frmcust0010PTankInfo.xml", args, 500, 308, "", -1, -1);
	}
}

/* 그리드 Enter Key 이동 */
function lfn_GridEnterMove(obj, nChar, bShift) {
	if (nChar == 13) {
		if(bshift=1) {
			obj.MoveToPrevCell();
		} else { 
			obj.MoveToNextCell();
		}
	}
}

// 관계자 Enter Key 이동
