/*
 * 기능 : 검색을 위한 대상물명 변환
 * parameter : 대상물명
 * return : 대상물명(BSEARCHNM)
 */
function lfn_ReplaceBnm(sBnm) {
    var sBSEARCHNM = sBnm;
    ds_oBnmCode.ClearData();
    
    tit_clearActionInfo();
    tit_addCsvSearchActionInfo("cust:searchCust0010BnmReplaceCode", false);
    // 서버 호출
    tit_callService(
        ""
        , ""
        , "" // inDs
        , "ds_oBnmCode=ds_oBnmCode" // outDs
        , ""
        , ""
        ,false
        ,false
        ,true
    );
    
    for (i=0;i<ds_oBnmCode.GetCount();i++) {
        sBSEARCHNM = Replace(sBSEARCHNM, ds_oBnmCode.GetColumn(i, "CCCDNM"), "");
    }
    
    return sBSEARCHNM;
}

function btnSearchNewZipCode_OnClick(obj)
{
	btnSearchOldZipCode_OnClick();
}

function radAddrGubun_OnChanged(obj,strCode,strText,nOldIndex,nNewIndex)
{
	if (strCode == "0") {
		btnSearchOldZipCode.Enable = true;
		edOldAddr1.Enable = true;
		edOldAddr2.Enable = true;
		btnSearchNewZipCode.Enable = false;
		edNewAddr1.Enable = false;
		edNewAddr2.Enable = false;
	
	} else {
		btnSearchOldZipCode.Enable = false;
		edOldAddr1.Enable = false;
		edOldAddr2.Enable = false;
		btnSearchNewZipCode.Enable = true;
		edNewAddr1.Enable = true;
		edNewAddr2.Enable = true;
	}
}
