Window.Script:
/*
 * 프로그램명 : frmcust0010PEduNoticeList.xml
 * 설명 : 교육통지서 발행이력
 * 작성일 : 2009.12.29
 * 작성자 : 김은규
 */

#include "LIB::cust.js"; // 공통업무 관련

var CMGNO = "";
/* 공통 필수 시작 */
function Form_OnLoadCompleted(obj) {
    gfn_Form_OnLoadCompleted(obj); // 공통 폼 로딩 이벤트 처리

    // 공통 코드 조회


    if (IsExistVar("sCmgno")) {
        if (gfn_IsNull(sCmgno)) {
            gfn_Alert("고객정보가 없습니다.");
            Close();
        }
        CMGNO = sCmgno;
        lfn_Search();
    }
}

function Form_OnUnloadCompleted(obj) {
    Close();
}

/*
 * 기능 : 종료
 */
function lfn_End(obj) {
    Close();
}

/*
 * 기능 : 조회
 */
function lfn_Search(obj) {
    ds_oEduNoticeList.ClearData(); // 데이타셋 초기화

    tit_clearActionInfo();
	tit_addSearchActionInfo("cust:searchCust0010PEduNoticeList", false);
	// 서버 호출 
	tit_callService(
		""
		,""
		,""	// inDs
		,"ds_oEduNoticeList=ds_oEduNoticeList"	// outDs
		,"CMGNO=" + quote(lpad(CMGNO,'0',13))
		,"lfn_AfterSearchEduNoticeList"
		,false
	);
}

/* 공통 필수 종료 */

/*
 * 기능 : 조회 후 처리해야 할 내용
 * parameter : nErrorCode - 에러코드
 *             strErrorMsg - 에러메시지
 * Return : 없음
 */
function lfn_AfterSearchEduNoticeList(nErrorCode, strErrorMsg) {
    if (nErrorCode != 0) { // 트랜잭션 에러를 처리한다.
        gfn_ErrorMsg(nErrorCode, strErrorMsg);
        return;
    }

    gfn_SearchMessage(gdEduNoticeList); // 조회 메세지를 처리하고 그리드의 정렬 헤드 마크를 제거한다.
}
